a!localVariables(
  local!sectionId: if(
    a!isNullOrEmpty(ri!SectionInstance),
    null,
    a!defaultValue(
      ri!SectionInstance[recordType!AIA Extraction Instance Section.sectionId],
      - 1
    )
  ),
  local!fields: filter(
    rule!AIA_Extraction_DisplayPromptField(
      sectionFilter: ri!sectionFilter,
      sectionId: local!sectionId,
      field: _,
      operation: ri!operation
    ),
    ri!modelVersion[recordType!AIA Extraction Model Version.fields]
  ),
  local!role: a!defaultValue(
    ri!modelVersion[recordType!AIA Extraction Model Version.role],
    rule!AIA_Extraction_DefaultPromptRole()
  ),
  local!instructions: rule!AIA_Extraction_PromptInstructions(
    modelVersion: ri!modelVersion
  ) & a!match(
    value: true,
    whenTrue: ri!hasBoundingBoxes,
    then: char(10) & rule!AIA_Extraction_BoundingBoxesInstructions(),
    whenTrue: ri!operation = cons!AIA_PROMPT_OPERATION_MODE_MULTIMODAL,
    then: char(10) & rule!AIA_Extraction_VisionInstructions(),
    default: ""
  ),
  local!examples: 
  /*if(*/
    /*ri!modelVersion['recordType!{f00f33ce-439e-4c81-9931-a244a643c0e8}AIA Extraction Model Version.fields.{71248595-92d7-42c5-b216-285a4e91704e}modelVersionId']=112,*/
    /*rule!S_GeneratePromptExamplesFromPreviousInstance(*/
      /*modelVersion: ri!modelVersion,*/
      /*fields: local!fields*/
    /*),*/
    rule!AIA_Extraction_GeneratePromptExamples(
      modelVersion: ri!modelVersion,
      fields: local!fields
    /*)*/
  ),
  local!entities: if(
    a!isNotNullOrEmpty(local!fields),
    rule!AIA_Extraction_GeneratePromptEntities(
      fields: local!fields,
      mode: if(
        and(
          ri!hasBoundingBoxes,
          ri!operation <> cons!AIA_PROMPT_OPERATION_MODE_MULTIMODAL
        ),
        cons!AIA_SCHEMA_MODE_SPAN,
        cons!AIA_SCHEMA_MODE_PLAIN
      )
    ),
    null
  ),
  a!map(
    role: local!role,
    instructions: local!instructions,
    entities: local!entities,
    examples: local!examples
  )
)

