a!localVariables(
  local!filters: append(
    ri!filters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: ri!createdOnField,
      operator: "between",
      value: {
        ri!selectedInterval.currrentStartDate,
        ri!selectedInterval.currentEndDate
      },
      applyWhen: a!isNotNullOrEmpty(ri!selectedInterval)
    )
  ),
  /*Data for Total Consumed Token KPI*/
  local!totalTokensConsumed: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: ri!recordType,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: ri!outputTokenField,
            function: "SUM",
            alias: "outputToken",
            filters: local!filters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!inputTokenField,
            function: "SUM",
            alias: "inputToken",
            filters: local!filters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!pageCountField,
            function: "SUM",
            alias: "pageCount",
            filters: local!filters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!instanceIdField,
            function: "COUNT",
            alias: "totalInstance",
            filters: local!filters
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    refreshAfter: "RECORD_ACTION"
  ),
  if(
    sum(
      local!totalTokensConsumed.inputToken,
      local!totalTokensConsumed.outputToken
    ) = 0,
    rule!AIA_UTIL_NoDataAvailableForKPIs(brandingMap: ri!brandingMap),
    {
      /*Total Count Across total instance and pages*/
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "ABOVE",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: fixed(
                    sum(
                      local!totalTokensConsumed.inputToken,
                      local!totalTokensConsumed.outputToken
                    ),
                    0,
                    false
                  ),
                  size: "MEDIUM_PLUS",
                  style: { "STRONG" }
                )
              },
              marginBelow: "NONE"
            ),
            width: "MINIMIZE"
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            showWhen: local!totalTokensConsumed.totalInstance <> 1,
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "ABOVE",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    #"urn:appian:translation-string:v1:801753b2-1341-4de4-92de-091ffb763a5b"(
                      #"urn:appian:translation-variable:v1:43b02cea-b534-4016-b58a-a1e0cf13e37f": local!totalTokensConsumed.totalInstance,
                      #"urn:appian:translation-variable:v1:18ca3961-87b3-4d35-ae9f-9d71f0341d52": local!totalTokensConsumed.pageCount
                    )
                  },
                  color: ri!brandingMap.Gray3,
                  size: "STANDARD"
                )
              },
              marginBelow: "EVEN_LESS"
            )
          )
        },
        alignVertical: "BOTTOM",
        marginBelow: "NONE"
      ),
      /*Bar Chart for Input and Output Tokens*/
      #"SYSTEM_SYSRULES_barChartField_v1"(
        label: #"urn:appian:translation-string:v1:c85d75d0-b3cb-48e8-8f2e-0d67a65fe1eb",
        labelPosition: "COLLAPSED",
        categories: {
          #"urn:appian:translation-string:v1:b78efab7-03d5-4c4d-b19f-2b8fb77ab6f4"
        },
        series: {
          #"SYSTEM_SYSRULES_chartSeries"(
            label: #"urn:appian:translation-string:v1:0d0de93c-9bcc-4f59-9725-602644f08bb4",
            data: local!totalTokensConsumed.inputToken,
            color: ri!brandingMap.chartColorSix
          ),
          #"SYSTEM_SYSRULES_chartSeries"(
            label: #"urn:appian:translation-string:v1:0ffc200f-4088-4aa5-bda2-ceefcfc53c1f",
            data: local!totalTokensConsumed.outputToken,
            color: ri!brandingMap.chartColorOne
          )
        },
        stacking: "NORMAL",
        height: "MICRO",
        xAxisStyle: "NONE",
        yAxisStyle: "NONE"
      )
    }
  )
)

