_a-0000ed16-a792-8000-9c64-011c48011c48_.sail_code:
a!localVariables(
  local!brandingMap: rule!AIA_UTIL_LoadBrandingMap(),
  local!logicalExpression: ri!logicalExpression,
  /*Query Rule Operations if not provided*/
  local!ruleOperations: a!defaultValue(
    value: ri!ruleOperations,
    default: rule!AIA_RULE_ENUM_RuleOperations()
  ),
  local!operationDisplayRule: a!refreshVariable(
    refreshAlways: true,
    value: displayvalue(
      local!logicalExpression.ruleOperationId,
      local!ruleOperations.id,
      local!ruleOperations.configDisplayRule,
      null
    )
  ),
  local!nestedExpresions: reject(
    a!isNullOrEmpty,
    append(
      local!logicalExpression.conditions,
      local!logicalExpression.expressions
    )
  ),
  /*Determine the output based on expression operator*/
  a!match(
    value: upper(
      local!logicalExpression.expressionOperator
    ),
    whenTrue: or(fv!value = "AND", fv!value = "OR"),
    then: #"SYSTEM_SYSRULES_forEach"(
      local!nestedExpresions,
      {
        rule!AIA_RULE_DisplayValidationConfigRecursive(
          logicalExpression: fv!item,
          ruleOperations: local!ruleOperations,
          isForInstance: ri!isForInstance
        ),
        if(
          fv!isLast,
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: " " & fv!value & " ",
            color: if(
              ri!isForInstance = true(),
              local!brandingMap.Gray3,
              local!brandingMap.Gray4
            )
          )
        )
      }
    ),
    whenTrue: a!isNotNullOrEmpty(value: local!operationDisplayRule),
    then: local!operationDisplayRule(
      logicalExpression: ri!logicalExpression,
      showMoreLink: ri!showMoreLink,
      isReturnOnlyText: ri!isReturnOnlyText
    ),
    default: if(
      a!isNotNullOrEmpty(ri!logicalExpression),
      #"SYSTEM_SYSRULES_toJson_v1"(ri!logicalExpression),
      null
    )
  )
)

