_a-0000eec9-5676-8000-9c92-011c48011c48_.sail_code:
a!localVariables(
  local!intervalList: rule!AIA_getIntervalsForDateFilter(),
  local!selectedInterval: index(local!intervalList, 1, null()),
  /*date based aggregation of automationed vs total docs*/
  local!automationRateAggregation: if(
    a!isNullOrEmpty(ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]),
    cast(typeof({a!map()}),null),
    #"SYSTEM_SYSRULES_queryRecordType_v3"(
      recordType: recordType!AIA Extraction Instance,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        groupings: #"SYSTEM_SYSRULES_grouping"(
          alias: "date",
          field: recordType!AIA Extraction Instance.createdOn,
          interval: local!selectedInterval.aggregationInterval
        ),
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            alias: "automatedCount",
            field: recordType!AIA Extraction Instance.id,
            function: "COUNT",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Extraction Instance.wasStp,
              operator: "=",
              value: true
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            alias: "totalCount",
            field: recordType!AIA Extraction Instance.id,
            function: "COUNT"
          )
        }
      ),
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.modelVersionId,
          operator: "=",
          value: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.createdOn,
          operator: "between",
          value: {
            local!selectedInterval.currrentStartDate,
            local!selectedInterval.currentEndDate
          }
        )
      },
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: a!defaultValue(
          value: rule!SHRD_UTIL_length(local!selectedInterval.intervalDates),
          default: 30
        )
      )
    ).data
  ),
  /*aggregation of stp docs accuracy (no date baseed grouping)*/
  local!errorRateAggregation: if(
    a!isNullOrEmpty(local!automationRateAggregation),
    /*if no automated docs, dont bother querying for error rate*/
    cast(typeof(a!map()),null),
    cast(typeof(a!map()),#"SYSTEM_SYSRULES_queryRecordType_v3"(
      recordType: recordType!AIA Extraction Instance,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            alias: "inaccurateCount",
            field: recordType!AIA Extraction Instance.id,
            function: "COUNT",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Extraction Instance.accuracy,
                operator: "<>",
                value: 1
              )
            }
          ),
          #"SYSTEM_SYSRULES_measure"(
            alias: "totalCount",
            field: recordType!AIA Extraction Instance.id,
            function: "COUNT"
          )
        }
      ),
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.modelVersionId,
          operator: "=",
          value: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.wasStp,
          operator: "=",
          value: true
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.statusId,
          operator: "in",
          value: cons!AIA_INSTANCE_STATUS_RECONCILED_ALL
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance.createdOn,
          operator: "between",
          value: {
            local!selectedInterval.currrentStartDate,
            local!selectedInterval.currentEndDate
          }
        )
      },
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1
      )
    ).data)
  ),
  rule!AIA_Template_CardWithTitle(
    title: #"urn:appian:translation-string:v1:4f1bf883-ac72-4816-87b7-8fbfd96be22e",
    contents: {
      if(
        a!isNullOrEmpty(local!automationRateAggregation),
        /*empty state*/
        {
          #"SYSTEM_SYSRULES_sectionLayout_v1"(),
          rule!AIA_UTIL_NoDataAvailableForKPIs(brandingMap: ri!brandingMap),
          #"SYSTEM_SYSRULES_sectionLayout_v1"()
        },
        /*metrics*/
        {
          rule!AIA_Template_GreyCardWithInnerTitle(
            title: #"urn:appian:translation-string:v1:f426db28-5049-4132-a2da-e03e66de1db2",
            caption: #"urn:appian:translation-string:v1:d1f6a8a4-c06f-46b5-9199-2a7889834993",
            brandingMap: ri!brandingMap,
            height: "AUTO",
            headingTag: "H4",
            headerSize: "EXTRA_SMALL",
            contents: {
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                      labelPosition: "COLLAPSED",
                      value: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: rule!AIA_DisplayPercentage(
                            value: sum(local!automationRateAggregation.automatedCount)/sum(local!automationRateAggregation.totalCount)
                          ),
                          color: ri!brandingMap.Gray4,
                          size: "LARGE",
                          style: { "STRONG" }
                        )
                      },
                      marginAbove: "NONE"
                    ),
                    width: "MINIMIZE"
                  ),
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                      labelPosition: "COLLAPSED",
                      value: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
