                    data: #"SYSTEM_SYSRULES_forEach"(
                      items: local!selectedInterval.intervalDates,
                      expression: a!localVariables(
                        local!aggregationValue: rule!AIA_UTIL_Filter(
                          records: local!automationRateAggregation,
                          field: "date",
                          value: fv!item
                        ),
                        if(
                          a!isNullOrEmpty(local!aggregationValue),
                          /*if no data for this date, return null (0 implies failed automation rate*/
