_a-0000ed69-99d8-8000-9ba6-011c48011c48_.sail_code:
a!localVariables(
  local!desiredAccuracy: ri!currentAccuracy + cons!AIA_INT_AREA_IMPROVEMENT_HIGH_RANGE_THRESHOLD_VALUE,
  local!data: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          /*Total Active Model Count*/
          #"SYSTEM_SYSRULES_measure"(
            alias: "activeModelCount",
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Model.publishedAccuracy,
                operator: ">=",
                value: local!desiredAccuracy
              )
            }
          ),
          #"SYSTEM_SYSRULES_measure"(
            alias: "allModelCount",
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            filters: {}
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null
  ),
  round(
    (
      local!data.activeModelCount / local!data.allModelCount
    ) * 100,
    2
  )
)

_a-0000ec1e-9975-8000-9bd6-011c48011c48_.sail_code:
with(
  local!modelVersionId: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Extraction Model Version,
      filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AIA Extraction Model Version.modelId,
            operator: "=",
            value: ri!modelId
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AIA Extraction Model Version.extractionModel.key,
            operator: "=",
            value: ri!modelKey
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AIA Extraction Model Version.status,
            operator: "=",
            value: cons!AIA_MODEL_VERSION_STATUS_PUBLISHED
          )
        },
        ignoreFiltersWithEmptyValues: true
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 1)
    ).data[recordType!AIA Extraction Model Version.modelVersionId],
    1,
    null
  ),
  if(
    a!isNullOrEmpty(local!modelVersionId),
    null,
    rule!AIA_Extraction_GetModelVersionById(
      identifier: local!modelVersionId
    )
  )
)

_a-0000ed16-a792-8000-9c64-011c48011c48_.sail_code:
