                      #"urn:appian:translation-string:v1:3b3337f9-f77b-43b1-9815-d1196d503531"(#"urn:appian:translation-variable:v1:515e4087-2e9c-4c5d-ba49-b66c9b0c6edc": rule!AIA_DisplayPercentage(
                        isPercentage: false,
                        value: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.samplingRate]
                      )),
                      #"urn:appian:translation-string:v1:50cd95e4-ca1a-4316-a5a2-93981a93608d"
                    ),
                    label: #"urn:appian:translation-string:v1:da73641e-3269-4f34-b570-c491ea3a7346",
                    link: #"SYSTEM_SYSRULES_recordActionField_v1"(
                      actions: #"SYSTEM_SYSRULES_recordActionItem"(
                        action: #"urn:appian:record-action:v1:f00f33ce-439e-4c81-9931-a244a643c0e8/eea965e0-752e-4646-a876-e8e586f73f79",
                        identifier: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]
                      ),
                      style: "LINKS",
                      display: "ICON",
                      align: "END"
                    ),
                    icon: "clipboard-check"
                  ),
                  rule!AIA_Template_CardWithIconAndText(
                    brandingMap: local!brandingMap,
                    color: "Yellow",
                    description: if(
                      ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.useAiReview],
                      a!localVariables(
                        local!aiFields: rule!AIA_UTIL_Filter(
                          records: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.fields],
                          field: recordType!AIA Extraction Model Version Field.useAiReview,
                          value: true,
                          filterType: "FILTER"
                        ),
                        local!fieldCount: length(
                          rule!AIA_UTIL_Filter(
                            records: local!aiFields,
                            field: recordType!AIA Extraction Model Version Field.type,
                            value: cons!AIA_EXTRACTION_FIELD_TYPE_TABLE,
                            filterType: "REJECT"
                          )
                        ),
                        local!tableCount: length(
                          rule!AIA_UTIL_Filter(
                            records: local!aiFields,
                            field: recordType!AIA Extraction Model Version Field.type,
                            value: cons!AIA_EXTRACTION_FIELD_TYPE_TABLE,
                            filterType: "FILTER"
                          )
                        ),
                        if(
                          or(
                            local!fieldCount > 0,
                            local!tableCount > 0
                          ),
                          joinarray(
                            reject(
                              fn!isnull,
                              {
                                #"urn:appian:translation-string:v1:d902fe0b-2dd1-4589-b89e-4abd341a8cb2",
                                rule!AIA_FieldsPluralization(
                                  fieldCount: local!fieldCount,
                                  includeVerb: false
                                ),
                                if(
                                  and(
                                    local!fieldCount > 0,
                                    local!tableCount > 0
                                  ),
                                  "and",
                                  null
                                ),
                                a!match(
                                  value: local!tableCount,
                                  whenTrue: fv!value <= 0,
                                  then: null,
                                  whenTrue: fv!value = 1,
                                  then: #"urn:appian:translation-string:v1:3002437c-5c08-479a-bcae-09a7527b7c9a"(#"urn:appian:translation-variable:v1:09330cab-96b1-4486-83c8-5d734d45e0d0": local!tableCount),
                                  default: #"urn:appian:translation-string:v1:b7a3d364-d884-4091-aa6c-084ec95ca419"(#"urn:appian:translation-variable:v1:ff0ea7cd-ef3c-4480-8e2c-1cba7eedc30e": local!tableCount)
                                )
                              }
                            ),
                            " "
                          ),
                          #"urn:appian:translation-string:v1:50cd95e4-ca1a-4316-a5a2-93981a93608d"
                        )
                      ),
                      #"urn:appian:translation-string:v1:50cd95e4-ca1a-4316-a5a2-93981a93608d"
                    ),
                    label: #"urn:appian:translation-string:v1:a67c9f85-f682-4191-bed6-c4912e7d88b4",
                    link: #"SYSTEM_SYSRULES_recordActionField_v1"(
                      actions: #"SYSTEM_SYSRULES_recordActionItem"(
                        action: #"urn:appian:record-action:v1:f00f33ce-439e-4c81-9931-a244a643c0e8/cd61305c-e2ed-401b-bab2-3768ce0e6986",
                        identifier: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]
                      ),
                      style: "LINKS",
                      display: "ICON",
                      align: "END"
                    ),
                    icon: "balance-scale"
                  )
                },
                brandingMap: local!brandingMap,
                cardStyle: "TRANSPARENT"
              )
            },
            width: "MEDIUM",
            backgroundColor: "TRANSPARENT",
            padding: "LESS"
          ),
          #"SYSTEM_SYSRULES_pane"(
            contents: {
              rule!AIA_Template_CardWithTitle(
                title: #"urn:appian:translation-string:v1:f10e7997-cb4c-4668-9506-a4c2f75e08ef",
                contents: rule!AIA_ExtractionModelVersion_ValidationRules_RecordGrid(
                  brandingMap: local!brandingMap,
                  modelVersionId: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.modelVersionId]
                ),
                brandingMap: local!brandingMap
              ),
              a!localVariables(
