                  )) = 0,
                  ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.useAiReview] <> true
                ),
                rule!AIA_Template_CardWithTitle(
                  title: concat(
                    #"urn:appian:translation-string:v1:a67c9f85-f682-4191-bed6-c4912e7d88b4",
                    if(
                      ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.useAiReview],
                      " - " & rule!AIA_GetLLMName(llm: ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.aiReviewLlm]),
                      ""
                    )
                  ),
                  contents: rule!AIA_ExtractionModelVersion_AiReviewSettings(
