  )
)

_a-0000ec1e-9975-8000-9bd6-011c48011c48_.sail_code:
a!localVariables(
  local!gridSelection: a!refreshVariable(value: {}, refreshAfter: "RECORD_ACTION"),
  #"SYSTEM_SYSRULES_gridField_v5"(
    showWhen: ri!showWhen,
    accessibilityText: #"urn:appian:translation-string:v1:e349de12-26ce-4f07-a2d6-818892336ae7"(
      #"urn:appian:translation-variable:v1:cd1d1493-da70-4c8d-a90a-53b50b533f19": a!defaultValue(
        value: ri!gridName,
        default: #"urn:appian:translation-string:v1:93ed6717-718e-4329-a6a7-80cd66ca9b9f"
      )
    ),
    labelPosition: "COLLAPSED",
    /*Instance Record Data*/
    data: #"SYSTEM_SYSRULES_recordData"(
      recordType: recordType!AIA Extraction Instance,
      filters: ri!filters,
      /*get latest reconciled event for LAst Reconciled column*/
      relatedRecordData: #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AIA Extraction Instance.eventHistory,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AIA Extraction Instance Event History.eventTypeId,
          operator: "in",
          value: {
            cons!AIA_EXTRACTION_INSTANCE_EVENT_TYPES[7]
          }
        ),
        limit: 1,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AIA Extraction Instance Event History.timestamp,
          ascending: false
        )
      )
    ),
    refreshAfter: "RECORD_ACTION",
    refreshInterval: .5,
    /*Initial Sort on Instance Id*/
    initialSorts: {
      #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!AIA Extraction Instance.id,
        ascending: false
      )
    },
    /*User Filters*/
    userFilters: ri!userFilters,
    showSearchBox: a!defaultValue(value: ri!showSearchBox, default: true),
    showRefreshButton: a!defaultValue(
      value: ri!showRefreshButton,
      default: true
    ),
    borderStyle: "LIGHT",
    shadeAlternateRows: false(),
    spacing: "STANDARD",
    /*Grid Columns*/
    columns: {
      /*sampling review flag*/
      #"SYSTEM_SYSRULES_gridColumn"(
        showWhen: ri!showColumnSamplingFlag,
        width: "ICON",
        sortField: recordType!AIA Extraction Instance.isSamplingRequired,
        value: rule!AIA_richTextDisplaySamplingFlag(
          Instance: fv!row,
          isSpotCheckRequiredField: recordType!AIA Extraction Instance.isSamplingRequired,
          brandingMap: ri!brandingMap,
          labelLocation: "CAPTION"
        )
      ),
      /*Instance -> Document Name with Link, Test Case, Id and Version Number*/
      #"SYSTEM_SYSRULES_gridColumn"(
        width: "4X",
        label: #"urn:appian:translation-string:v1:11c554c3-1e1c-43a1-9ac8-a846f702995f",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            /*Document Name with Link*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_FormatDocument(instance: fv!row),
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!AIA Extraction Instance,
                identifier: fv!identifier
              ),
              linkStyle: "STANDALONE",
              color: ri!brandingMap.Gray4,
              style: "STRONG"
            ),
            char(10),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_UTIL_displayRequestId(
                requestId: fv!row[recordType!AIA Extraction Instance.id]
              ) & cons!SHRD_DOT_SEPARATOR,
              color: ri!brandingMap.Gray3,
              size: "SMALL"
            ),
            /*Model Name if model filter provided*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              showWhen: contains(
                ri!userFilters,
                #"urn:appian:user-filter:v1:64322398-e457-4695-87d5-09794164cf77/4150c060-fbf9-4b2b-8ad1-9c0a30c5858e"
              ),
              text: rule!AIA_ModelVersionName(
                ModelVersion: fv!row[recordType!AIA Extraction Instance.modelVersion]
              ),
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!AIA Extraction Model Version,
                identifier: fv!row[recordType!AIA Extraction Instance.modelVersionId],
                targetLocation: #"urn:appian:site-page:v1:15ad6b1a-7c0a-47f8-8803-ac74b16df0df/63100729-14fc-41a7-875c-acad7e90fa10"
              ),
              linkStyle: "STANDALONE",
              size: "SMALL"
            ),
            /*Version number if no model filter (assume all instances from a single model)*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              showWhen: not(
                contains(
                  ri!userFilters,
                  #"urn:appian:user-filter:v1:64322398-e457-4695-87d5-09794164cf77/4150c060-fbf9-4b2b-8ad1-9c0a30c5858e"
                )
              ),
              text: #"urn:appian:translation-string:v1:0dc23859-ec54-488e-8e0e-54f234016331"(
                #"urn:appian:translation-variable:v1:6432df6d-081e-454e-a970-7f0e3444fc73": fv!row[recordType!AIA Extraction Instance.modelVersion.versionId]
              ),
              color: ri!brandingMap.Gray3,
              size: "SMALL"
            ),
            /*additional display for test case*/
            if(
              fv!row[recordType!AIA Extraction Instance.testInstance],
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: cons!SHRD_DOT_SEPARATOR,
                    color: ri!brandingMap.Gray3,
                    size: "SMALL"
                  ),
                  /*check for warnings for test instances to display*/
                  a!match(
                    /*some value just to allow for match to run*/
                    value: 1,
                    /*not reconciled (relatedRecordData will have returned 1 entry if a reconciled event occured)*/
                    whenTrue: a!isNullOrEmpty(fv!row[recordType!AIA Extraction Instance.eventHistory]),
                    then: {
                      #"SYSTEM_SYSRULES_richTextIcon"(
                        icon: "exclamation-triangle",
                        color: ri!brandingMap.Orange3
                      )," ",
                      #"urn:appian:translation-string:v1:dec2dc97-0a68-4a75-8f42-f29a622d7242"
                    },
                    /*instance fields don't match latest model fields*/
                    whenTrue: fv!row[recordType!AIA Extraction Instance.modelVersion.extractionModel.fieldsCount] <> fv!row[recordType!AIA Extraction Instance.totalFieldCount],
                    then: {
                      #"SYSTEM_SYSRULES_richTextIcon"(
                        icon: "exclamation-triangle",
                        color: ri!brandingMap.Orange3
                      )," ",
                      #"urn:appian:translation-string:v1:47658624-0ce2-4589-bef2-ce93c76e9187"
                    },
                    /*stanrad test case display*/
                    default: {
                      #"SYSTEM_SYSRULES_richTextIcon"(icon: "flask"),
                      " ",
                      #"urn:appian:translation-string:v1:21705970-1db4-4bc0-9664-f32b86194327"
                    }
                  )

                },
                color: ri!brandingMap.Gray3,
                size: "SMALL"
              ),
              {}
            )
          }
        ),

      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        value: rule!AIA_ViewDocumentAction(
          document: fv!row[recordType!AIA Extraction Instance.documentId]
        ),
        align: "CENTER",
        width: "ICON"
      ),
      /*Field Count*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:15d21e83-aadd-4b1f-9cdf-d9db8556efc3",
        align: "CENTER",
        width: "1X",
        showWhen: a!defaultValue(
          value: ri!showColumnFieldCount,
          default: false
        ),
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            if(
              fv!row[recordType!AIA Extraction Instance.modelVersion.extractionModel.fieldsCount] = fv!row[recordType!AIA Extraction Instance.totalFieldCount],
              {},
              {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "exclamation-triangle",
                  caption: #"urn:appian:translation-string:v1:acbac9a0-c26f-4934-afa4-3cb593900c69",
                  color: ri!brandingMap.Orange3
                ),
                " "
              }
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: fv!row[recordType!AIA Extraction Instance.totalFieldCount],
              color: ri!brandingMap.Gray4
            )
          }
        )
      ),
      /*Status*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:34413645-ef22-4a94-8709-e4cae57e6a92",
        showWhen: ri!showColumnStatus,
        width: "2X",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: fv!row[recordType!AIA Extraction Instance.refInstanceStatus.icon],
                color: index(
                  ri!brandingMap,
                  fv!row[recordType!AIA Extraction Instance.refInstanceStatus.iconColor],
                  null
                ),
                altText: fv!row[recordType!AIA Extraction Instance.refInstanceStatus.label],
                caption: fv!row[recordType!AIA Extraction Instance.refInstanceStatus.label],

              ),
              " ",
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: fv!row[recordType!AIA Extraction Instance.refInstanceStatus.label],

              ),
              rule!AIA_Instance_ValidationTotal(
                row: fv!row,
                field: recordType!AIA Extraction Instance.validationErrors,
                brandingMap: ri!brandingMap
              )
            }
          )
        )
      ),
      /* Errors */
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:1d6180d6-3a8d-4d4a-a129-a6697e901374",
        value: rule!AIA_ValidationErrorsGridDisplay(
          validationErrors: fv!row[recordType!AIA Extraction Instance.validationErrors],
          brandingMap: ri!brandingMap
        ),
        align: "CENTER",
        width: "2X",
        showWhen: a!defaultValue(
          value: ri!showColumnErrorCount,
          default: cons!AIA_FLAG_SHOW_INSTANCE_ERROR_COLUMN
        )
      ),
      /* Accuracy */
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:1d85cd95-b854-411a-907a-67f3ec9b03bd",
        showWhen: ri!showColumnAccuracy,
        align: "CENTER",
        width: "2X",
        sortField: recordType!AIA Extraction Instance.accuracy,
        helpTooltip: #"urn:appian:translation-string:v1:bdeb5486-da62-4dd1-acba-18b27f6019b3",
        value: a!localVariables(
          local!percentageAttributes: rule!AIA_UTIL_GetPercentageAttributes(
            brandingMap: ri!brandingMap,
            value: fv!row[recordType!AIA Extraction Instance.accuracy]
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: a!defaultValue(
                index(
                  local!percentageAttributes,
                  "percentage",
                  null()
                ),
                "-"
              ),
              color: index(
                local!percentageAttributes,
                "textColor",
                null()
              ),
              style: "STRONG"
            )
          )
        ),
        backgroundColor: index(
          rule!AIA_UTIL_GetPercentageAttributes(
            brandingMap: ri!brandingMap,
            value: fv!row[recordType!AIA Extraction Instance.accuracy]
          ),
          "backgroundColor",
          null()
        )
