a!localVariables(
  local!isExtraction: runtimetypeof(ri!modelVersion) = typeof(
    recordType!AIA Extraction Model Version()
  ),
  local!fields: if(
    local!isExtraction,
    a!map(
      relationship: recordType!AIA Extraction Model Version.examples
    ),
    a!map(
      relationship: recordType!AIA Classification Model Version.examples
    )
  ),
  local!hasExamples: a!isNotNullOrEmpty(
    reject(
      fn!isnull,
      {
        ri!modelVersion[local!fields.relationship][recordType!AIA Model Version Example.id]
      }
    )
  ),
  if(
    local!hasExamples,
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        cast(
          #"urn:appian:function:v1:a:listtype?okey=a!listType"(
            recordType!AIA Model Version Example
          ),
          #"SYSTEM_SYSRULES_queryEntity_v2"(
            entity: cons!AIA_DSE_MODEL_VERSION_EXAMPLE,
            query: #"SYSTEM_SYSRULES_query"(
              filter: #"SYSTEM_SYSRULES_queryFilter"(
                field: "id",
                operator: "in",
                value: ri!modelVersion[local!fields.relationship][recordType!AIA Model Version Example.id]
              ),
              pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 100)
            ),
            fetchTotalCount: false
          ).data
        ),
        if(
          or(
            not(local!isExtraction),
            a!match(
              value: rule!AIA_ExtractJson(
                json: fv!item[recordType!AIA Model Version Example.rule]
              ),
              whenTrue: or(
                a!isNullOrEmpty(fv!value),
                a!isNullOrEmpty(ri!fields)
              ),
              then: true,
              whenTrue: tostring(index(fv!value,"type",null())) = "FIELD",
              then: contains(
                touniformstring(
                  ri!fields[recordType!AIA Extraction Model Version Field.key]
                ),
                tostring(fv!value.field)
              ),
              default: true
            )
          ),
          fv!item,
          null
        )
      )
    ),
    null
  )
)

