_a-0000ecea-6c5f-8000-9c4b-011c48011c48_.sail_code:
a!match(
  value: ri!mode,
  whenTrue: a!isNullOrEmpty(ri!field[recordType!AIA Extraction Model Version Field.key]),
  then: false,
  equals: cons!AIA_SCHEMA_MODE_AI_REVIEW,
  then: a!defaultValue(ri!field[recordType!AIA Extraction Model Version Field.useAiReview], false) = true,
  equals: cons!AIA_PROMPT_MODE_CALCULATED,
  then: or(
    ri!field[recordType!AIA Extraction Model Version Field.extractionType] = cons!AIA_EXTRACTION_TYPES[4],
    and(
      ri!field[recordType!AIA Extraction Model Version Field.type] = cons!AIA_EXTRACTION_FIELD_TYPE_TABLE,
      contains(
        touniformstring(
          ri!field[recordType!AIA Extraction Model Version Field.columns.extractionType]
        ),
        cons!AIA_EXTRACTION_TYPES[4]
      )
    )
  ),
  default: and(
    /* Filter out field by section */
    or(
      and(
        a!isNullOrEmpty(ri!sectionFilter),
        a!isNullOrEmpty(ri!sectionId)
      ),
      and(
        a!isNotNullOrEmpty(ri!sectionFilter),
        a!isNotNullOrEmpty(
          ri!field[recordType!AIA Extraction Model Version Field.section]
        ),
        ri!field[recordType!AIA Extraction Model Version Field.section] = ri!sectionFilter
      ),
      or(
        and(
          a!isNotNullOrEmpty(ri!sectionId),
          a!isNotNullOrEmpty(
            ri!field[recordType!AIA Extraction Model Version Field.sectionId]
          ),
          ri!field[recordType!AIA Extraction Model Version Field.sectionId] = ri!sectionId
        ),
        and(
          a!isNotNullOrEmpty(ri!sectionId),
          a!isNullOrEmpty(
            ri!field[recordType!AIA Extraction Model Version Field.sectionId]
          ),
          ri!sectionId = - 1
        )
      )
    ),
    /* Filter out field by extraction type - Generative AI */
    or(
      ri!field[recordType!AIA Extraction Model Version Field.extractionType] = cons!AIA_EXTRACTION_TYPES[1],
      a!isNullOrEmpty(
        ri!field[recordType!AIA Extraction Model Version Field.extractionType]
      )
    ),
    /* Filter out field by operation type */
    or(
      /* Filter out field by operation type - Generative AI - Text */
      and(
        or(
          a!isNullOrEmpty(ri!operation),
          ri!operation = cons!AIA_PROMPT_OPERATION_MODE_TEXT
        ),
        or(
          a!isNullOrEmpty(ri!field[recordType!AIA Extraction Model Version Field.useVision]),
          ri!field[recordType!AIA Extraction Model Version Field.useVision] = false,
        )
      ),
      /* Filter out field by operation type - Generative AI - Vision */
      and(
        ri!operation = cons!AIA_PROMPT_OPERATION_MODE_MULTIMODAL,
        or(
          and(
            a!isNotNullOrEmpty(ri!field[recordType!AIA Extraction Model Version Field.useVision]),
            ri!field[recordType!AIA Extraction Model Version Field.useVision] = true
          ),
          and(
            a!isNotNullOrEmpty(ri!parentField[recordType!AIA Extraction Model Version Field.useVision]),
            ri!parentField[recordType!AIA Extraction Model Version Field.useVision] = true
          )
        )
      )
    )
  )
)

