"## Instructions

" & joinarray(
  reject(
    fn!isnull,
    {
      "- " & #"urn:appian:translation-string:v1:068d246a-b84d-42a2-a07b-02833e24e15c",
      "- " & #"urn:appian:translation-string:v1:bd52b8f6-1431-454e-875f-7e8b8ebe9da7",
      "- " & #"urn:appian:translation-string:v1:e3c91fec-48f5-4848-8406-e97856ad96af",
      "- " & #"urn:appian:translation-string:v1:361ff628-e4e9-431b-8472-ecadea9b11d8",
      if(
        or(
          ri!modelVersion[recordType!AIA Extraction Model Version.fields.isMultiple]
        ),
        "- " & #"urn:appian:translation-string:v1:925e0dd9-c0f8-45e7-af9f-916a29285ccd",
        null
      ),
      if(
        or(
          contains(
            ri!modelVersion[recordType!AIA Extraction Model Version.fields.type],
            {cons!AIA_EXTRACTION_FIELD_TYPES[2],cons!AIA_EXTRACTION_FIELD_TYPES[3]}
          ),
          contains(
            a!flatten(ri!modelVersion[recordType!AIA Extraction Model Version.fields.columns.type]),
            {cons!AIA_EXTRACTION_FIELD_TYPES[2],cons!AIA_EXTRACTION_FIELD_TYPES[3]}
          )
        ),
        "- " & #"urn:appian:translation-string:v1:8e6dd327-f952-4c3f-b8e3-8ce9acdc4345",
        null
      ),
      if(
        or(
          contains(
            ri!modelVersion[recordType!AIA Extraction Model Version.fields.type],
            cons!AIA_EXTRACTION_FIELD_TYPES[4]
          ),
          contains(
            a!flatten(ri!modelVersion[recordType!AIA Extraction Model Version.fields.columns.type]),
            cons!AIA_EXTRACTION_FIELD_TYPES[4]
          )
        ),
        "- " & #"urn:appian:translation-string:v1:27d6e065-3f9f-471b-b412-1d3847d474bc",
        null
      ),
      if(
        or(
          contains(
            ri!modelVersion[recordType!AIA Extraction Model Version.fields.type],
            cons!AIA_EXTRACTION_FIELD_TYPES[5]
          ),
          contains(
            a!flatten(ri!modelVersion[recordType!AIA Extraction Model Version.fields.columns.type]),
            cons!AIA_EXTRACTION_FIELD_TYPES[5]
          )
        ),
        "- " & #"urn:appian:translation-string:v1:5bae3763-38ac-423c-9336-2c6d82051a95",
        null
      ),
      if(
        any(
          a!isNotNullOrEmpty,
          a!flatten(
            {
              ri!modelVersion[recordType!AIA Extraction Model Version.fields.optionRule],
              ri!modelVersion[recordType!AIA Extraction Model Version.fields.columns.optionRule],
            }
          )
        ),
        "- " & #"urn:appian:translation-string:v1:c6d3ccf5-b428-4900-b98b-e8df8db90ce8",
        null
      ),
      "- Today's date is " & text(today(), "yyyy-MM-dd"),
      ri!modelVersion[recordType!AIA Extraction Model Version.instructions],
    }
  ),
  char(10)
)

