a!localVariables(
  local!previousFilters: append(
    ri!filters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: ri!createdOnField,
      operator: "between",
      value: {
        ri!selectedInterval.previousStartDate,
        ri!selectedInterval.previousEndDate
      },
      applyWhen: a!isNotNullOrEmpty(ri!selectedInterval)
    )
  ),
  local!currentFilters: append(
    ri!filters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: ri!createdOnField,
      operator: "between",
      value: {
        ri!selectedInterval.currrentStartDate,
        ri!selectedInterval.currentEndDate
      },
      applyWhen: a!isNotNullOrEmpty(ri!selectedInterval)
    )
  ),
  /*Data for Average Token Consumed KPI*/
  local!averageTokenConsumed: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: ri!recordType,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: ri!outputTokensField,
            function: "AVG",
            alias: "outputToken",
            filters: local!currentFilters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!inputTokensField,
            function: "AVG",
            alias: "inputToken",
            filters: local!currentFilters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!outputTokensField,
            function: "AVG",
            alias: "previousOutputToken",
            filters: local!previousFilters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!inputTokensField,
            function: "AVG",
            alias: "previousInputToken",
            filters: local!previousFilters
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    refreshAfter: "RECORD_ACTION"
  ),
  if(
    sum(
      local!averageTokenConsumed.inputToken,
      local!averageTokenConsumed.outputToken
    ) = 0,
    rule!AIA_UTIL_NoDataAvailableForKPIs(brandingMap: ri!brandingMap),
    {
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "ABOVE",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: fixed(
                    floor(
                      sum(
                        local!averageTokenConsumed.inputToken,
                        local!averageTokenConsumed.outputToken
                      )
                    ),
                    0,
                    false()
                  ),
                  size: "MEDIUM_PLUS",
                  style: { "STRONG" }
                )
              },
              marginBelow: "NONE"
            ),
            width: "MINIMIZE"
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_tagField"(
              labelPosition: "ABOVE",
              tags: a!localVariables(
                local!inputToken: if(
                  rule!AIA_UTIL_isBlank(
                    tointeger(
                      index(
                        local!averageTokenConsumed,
                        "inputToken",
                        null()
                      )
                    )
                  ),
                  0,
                  tointeger(
                    index(
                      local!averageTokenConsumed,
                      "inputToken",
                      null()
                    )
                  )
                ),
                local!outputToken: if(
                  rule!AIA_UTIL_isBlank(
                    tointeger(
                      index(
                        local!averageTokenConsumed,
                        "outputToken",
                        null()
                      )
                    )
                  ),
                  0,
                  tointeger(
                    index(
                      local!averageTokenConsumed,
                      "outputToken",
                      null()
                    )
                  )
                ),
                local!previousInputToken: if(
                  rule!AIA_UTIL_isBlank(
                    tointeger(
                      index(
                        local!averageTokenConsumed,
                        "previousInputToken",
                        null()
                      )
                    )
                  ),
                  0,
                  tointeger(
                    index(
                      local!averageTokenConsumed,
                      "previousInputToken",
                      null()
                    )
                  )
                ),
                local!previousOutputToken: if(
                  rule!AIA_UTIL_isBlank(
                    tointeger(
                      index(
                        local!averageTokenConsumed,
                        "previousOutputToken",
                        null()
                      )
                    )
                  ),
                  0,
                  tointeger(
                    index(
                      local!averageTokenConsumed,
                      "previousOutputToken",
                      null()
                    )
                  )
                ),
                local!value: a!defaultValue(
                  rule!AIA_UTIL_findAverageRaiseGivenInputNumbers(
                    lastMonthYear: sum(
                      local!previousInputToken,
                      local!previousOutputToken
                    ),
                    thisMonthYear: sum(local!inputToken, local!outputToken)
                  ),
                  0
                ),
                {
                  if(
                    local!value = 0,
                    #"SYSTEM_SYSRULES_tagItem_v1"(
                      text: concat(
                        local!value,
                        "%"
                      ),
                      backgroundColor: ri!brandingMap.Gray0,
                      textColor: ri!brandingMap.Gray4
                    ),
                    #"SYSTEM_SYSRULES_tagItem_v1"(
                      text: concat(
                        if(local!value > 0, "+", ""),
                        local!value,
                        "%"
                      ),
                      backgroundColor: if(
                        local!value > 0,
                        ri!brandingMap.Green1,
                        ri!brandingMap.Red1
                      ),
                      textColor: if(
                        local!value > 0,
                        ri!brandingMap.Green4,
                        ri!brandingMap.Red4
                      )
                    )
                  )
                }
              ),
              size: "STANDARD",
              marginBelow: "NONE"
            ),
            width: "MINIMIZE"
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "ABOVE",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: { ri!selectedInterval.secondaryKPILabel },
                  color: ri!brandingMap.Gray3,
                  size: "STANDARD"
                )
              },
              marginBelow: "NONE"
            )
          )
        },
        alignVertical: "BOTTOM",
        marginBelow: "NONE"
      ),
      /*Bar Chart for Input and Output Tokens*/
      #"SYSTEM_SYSRULES_barChartField_v1"(
        label: #"urn:appian:translation-string:v1:bcc457a4-63d3-464e-82ee-637187d7b41e",
        labelPosition: "COLLAPSED",
        categories: {
          #"urn:appian:translation-string:v1:b78efab7-03d5-4c4d-b19f-2b8fb77ab6f4"
        },
        series: {
          #"SYSTEM_SYSRULES_chartSeries"(
            label: #"urn:appian:translation-string:v1:0d0de93c-9bcc-4f59-9725-602644f08bb4",
            data: floor(local!averageTokenConsumed.inputToken),
            color: ri!brandingMap.chartColorSix
          ),
          #"SYSTEM_SYSRULES_chartSeries"(
            label: #"urn:appian:translation-string:v1:0ffc200f-4088-4aa5-bda2-ceefcfc53c1f",
            data: floor(local!averageTokenConsumed.outputToken),
            color: ri!brandingMap.chartColorOne
          )
        },
        stacking: "NORMAL",
        height: "MICRO",
        xAxisStyle: "NONE",
        yAxisStyle: "NONE"
      )
    }
  )
)

