a!localVariables(
  /*Model Version Id*/
  local!modelVersionId: if(
    a!isNullOrEmpty(ri!modelVersionId),
    0,
    ri!modelVersionId
  ),
  /*Interval List Choice Values*/
  local!intervalList: rule!AIA_getIntervalsForDateFilter(),
  /*Selected Interval Value : Last 30 days by deafult*/
  local!selectedIntervalValue: index(local!intervalList, 1, null()),
  /*KPI Labels and Helptool tips*/
  local!kpiLabelAndTips: {
    a!map(
      id: 1,
      label: #"urn:appian:translation-string:v1:c85d75d0-b3cb-48e8-8f2e-0d67a65fe1eb",
      helptoolTip: #"urn:appian:translation-string:v1:d2937b2f-d2f6-4972-aa8e-ab9eb9d0faed"
    ),
    a!map(
      id: 2,
      label: #"urn:appian:translation-string:v1:6bdd7618-e3bb-4b70-ae6d-ff86f2fae58c",
      helptoolTip: {}
    ),
    a!map(
      id: 3,
      label: #"urn:appian:translation-string:v1:bcc457a4-63d3-464e-82ee-637187d7b41e",
      helptoolTip: {}
    )
  },
  {
    /*Heading and Interval Filter*/
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      alignVertical: "MIDDLE",
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          width: "3X",
          item: #"SYSTEM_SYSRULES_headingField"(
            marginAbove: "STANDARD",
            fontWeight: "SEMI_BOLD",
            text: #"urn:appian:translation-string:v1:ec270ab9-6512-48ea-a3d2-1040ee7259ce",
            size: "SMALL",
            headingTag: "H2",
            color: ri!brandingMap.Gray4
          )
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          width: "1X",
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            align: "RIGHT",
            accessibilityText: #"urn:appian:translation-string:v1:b1c89ba1-87c5-4d8d-b8f3-b5f4960e19ec",
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: #"urn:appian:translation-string:v1:b1c89ba1-87c5-4d8d-b8f3-b5f4960e19ec",
                style: "STRONG",
                color: ri!brandingMap.Gray4
              )
            }
          )
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          width: "1X",
          item: #"SYSTEM_SYSRULES_dropdownField_v1"(
            labelPosition: "COLLAPSED",
            label: #"urn:appian:translation-string:v1:b1c89ba1-87c5-4d8d-b8f3-b5f4960e19ec",
            choiceLabels: { local!intervalList.label },
            choiceValues: { local!intervalList },
            saveInto: { local!selectedIntervalValue },
            value: local!selectedIntervalValue
          )
        )
      }
    ),
    /*KPI Contents*/
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: #"SYSTEM_SYSRULES_forEach"(
        items: local!kpiLabelAndTips,
        expression: #"SYSTEM_SYSRULES_columnLayout"(
          contents: rule!AIA_Template_GreyCardWithInnerTitle(
            title: fv!item.label,
            caption: fv!item.helptoolTip,
            brandingMap: ri!brandingMap,
            contents: {
              a!match(
                value: fv!index,
                equals: 1,
                then: rule!AIA_Extraction_TotalTokensConsumed(
                  brandingMap: ri!brandingMap,
                  selectedInterval: local!selectedIntervalValue,
                  filters: {
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AIA Extraction Instance.modelVersionId,
                      operator: "=",
                      value: ri!modelVersionId
                    )
                  }
                ),
                equals: 2,
                then: rule!AIA_Extraction_TokenTrend(
                  brandingMap: ri!brandingMap,
                  selectedInterval: local!selectedIntervalValue,
                  filters: {
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AIA Extraction Instance.modelVersionId,
                      operator: "=",
                      value: ri!modelVersionId
                    )
                  }
                ),
                equals: 3,
                then: rule!AIA_Extraction_AIA_AverageTokensByRequest(
                  filters: {
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AIA Extraction Instance.modelVersionId,
                      operator: "=",
                      value: ri!modelVersionId
                    )
                  },
                  brandingMap: ri!brandingMap,
                  selectedInterval: local!selectedIntervalValue
                ),
                default: {}
              )
            }
          )
        )
      )
    )
  }
)

