a!localVariables(
  /*Branding Data for styling*/
  local!brandingMap: rule!AIA_UTIL_LoadBrandingMap(),

  /*load latest run for default and to check if runs exist*/
  local!latestTestSuiteRunId: rule!AIA_ModelTestSuiteRun_GetLatestTestSuiteRunForModel(
    modelUuid: ri!extractionModel[recordType!AIA Extraction Model.uuid],
    fields: recordType!AIA Model Test Suite Run.testSuiteRunId
  )[recordType!AIA Model Test Suite Run.testSuiteRunId],

  /*default selected test suite to latest run*/
  local!selectedTestSuiteRunId: local!latestTestSuiteRunId,

  local!selectedTestSuiteRun: a!refreshVariable(
    refreshAfter: "RECORD_ACTION",
    value: rule!AIA_QR_getModelTestSuiteRun(
      returnType: "SINGLE_OBJECT",
      executeWhen: a!isNotNullOrEmpty(local!selectedTestSuiteRunId),
      modelTestSuiteRunIds: local!selectedTestSuiteRunId,
      fields: {
        recordType!AIA Model Test Suite Run.modelTestSuite
      }
    )
  ),
  local!isProcessRunning: a!refreshVariable(
    value: rule!AIA_QPA_getActiveInitiateExtractionModelDetails(
      modelKey: ri!extractionModel[recordType!AIA Extraction Model.key]
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  if(
    a!isNullOrEmpty(local!latestTestSuiteRunId),
