a!localVariables(
  /*Classification Model Data*/
  local!classificationModelData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            alias: "activeModelCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Model.publishedAccuracy,
            function: "AVG",
            alias: "accuracyAvg",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null()
  ),
  /*Classification Instance Data*/
  local!classificationInstanceData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Instance,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1),
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
