              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.inputTokens,
            function: "SUM",
            alias: "inputTokens",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.outputTokens,
            function: "SUM",
            alias: "outputTokens",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.pageCount,
            function: "SUM",
            alias: "pageCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          )
        }
      )
    ).data,
    1,
    null
  ),
  local!modelVersionIds: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Classification Model,
    fields: {
      recordType!AIA Classification Model.publishedModelVersionId
    },
    filters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AIA Classification Model.createdOn,
      operator: "between",
      value: {
        ri!selectedInterval.currrentStartDate,
        ri!selectedInterval.currentEndDate
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 5000)
  ).data[recordType!AIA Classification Model.publishedModelVersionId],
  /*Area of Improvement Data*/
  local!classificationImpData: a!refreshVariable(
    value: rule!AIA_HomePage_Classification_GetDataForAreaOfImprovements(
      brandingMap: ri!brandingMap,
      selectedInterval: ri!selectedInterval
    ),
    refreshAfter: "RECORD_ACTION"
