      measures: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!selectedInterval.weeklyGroups,
          expression: #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.accuracy,
            function: "AVG",
            alias: concat("avgTrend", fv!index),
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance.createdOn,
                operator: "between",
                value: {
                  todatetime(fv!item.startDate),
                  todatetime(fv!item.endDate)
