a!localVariables(
  local!desiredAccuracy: ri!currentAccuracy + cons!AIA_INT_AREA_IMPROVEMENT_HIGH_RANGE_THRESHOLD_VALUE,
  local!data: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Extraction Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          /*Total Active Model Count*/
          #"SYSTEM_SYSRULES_measure"(
            alias: "activeModelCount",
            field: recordType!AIA Extraction Model.modelId,
            function: "COUNT",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Extraction Model.publishedVersionAccuracy,
                operator: ">=",
                value: local!desiredAccuracy
              )
            }
          ),
          #"SYSTEM_SYSRULES_measure"(
            alias: "allModelCount",
            field: recordType!AIA Extraction Model.modelId,
            function: "COUNT",
            filters: {}
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null
  ),
  round(
    (
      local!data.activeModelCount / local!data.allModelCount
    ) * 100,
    2
  )
)

