a!localVariables(
  local!version: joinarray(
    #"SYSTEM_SYSRULES_forEach"(
      items: rdrop(ldrop(split(cons!AIA_VERSION, "."), 2), 1),
      expression: tointeger(fv!item)
    ),
    "."
  ),
  concat(
    cons!AIA_DOCUMENTATION_LINK,
    rule!AIA_GetSiteVersion(),
    "/aidc-",
    local!version,
    "/",
    ri!resource,
    if(find(".html", ri!resource) > 0, "", ".html"),
    if(
      a!isNotNullOrEmpty(ri!header),
      "#" & ri!header,
      ""
    )
  )
)

