    recordType: recordType!AIA Extraction Instance Field Row Cell,
    fields: {
      recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.statusId,
      recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.documentId,
      recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.createdOn,
      recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.createdBy
    },
