      operator: "AND",
      filters: {
        if(
          ri!isInstanceSummaryView,
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AIA Extraction Instance Field Row Cell.modelVersionFieldId,
            operator: "=",
            value: ri!extractionInstance[recordType!AIA Extraction Instance.fields.modelVersionFieldId]
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AIA Extraction Instance Field Row Cell.modelVersionFieldId,
            operator: "=",
            value: ri!extractionModelVersionField[recordType!AIA Extraction Model Version Field.fieldId]
          )
        )
      },
      ignoreFiltersWithEmptyValues: true
    )
  ),
  columns: {
    /*Status*/
    #"SYSTEM_SYSRULES_gridColumn"(
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: #"SYSTEM_SYSRULES_richTextIcon"(
            icon: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.refInstanceStatus.icon],
            color: index(
              ri!brandingMap,
              fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.refInstanceStatus.iconColor],
              null
            ),
            altText: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.refInstanceStatus.label],
            caption: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.refInstanceStatus.label],

          )
        ),
        align: "CENTER"
      ),
      width: "ICON",
      align: "CENTER"
    ),
    /*Instances column*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:11c554c3-1e1c-43a1-9ac8-a846f702995f",
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AIA_FormatDocument(
              instance: fv!row[#"urn:appian:record-relationship:v1:8337b66d-8cf1-439b-9727-e3d9336008a0/fe3a876b-768f-4803-a9d0-06814d9afddb/10a65722-aeb4-4477-b872-04c95da83156/86065ef5-da05-41ca-ac46-d659389907da"]
            ),
            link: #"SYSTEM_SYSRULES_recordLink"(
              recordType: recordType!AIA Extraction Instance,
              identifier: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.id]
            ),
            linkStyle: "STANDALONE",
            color: ri!brandingMap.Gray4,
            style: "STRONG"
          ),
          char(10),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AIA_UTIL_displayRequestId(
              requestId: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.id]
            ) & cons!SHRD_DOT_SEPARATOR & rule!AIA_UTIL_displayDateTime(
              isDisplayTime: false(),
              dateTime: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.createdOn]
            ) & " " & #"urn:appian:translation-string:v1:84daac47-757a-42af-be49-1c45a6070a6a" & " " & rule!AIA_UTIL_displayUserFullName(
              userName: fv!row[recordType!AIA Extraction Instance Field Row Cell.row.instanceField.extractionInstance.createdBy]
            ),
            color: ri!brandingMap.Gray3,
            size: "SMALL"
          )
        }
