          icon: "undo",
          caption: #"urn:appian:translation-string:v1:61dff5df-b06f-45e2-9d83-c1df59c410a6",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: {
              #"SYSTEM_SYSRULES_startProcess"(
                processModel: cons!AIA_PM_REVERT_FIELD,
                processParameters: {
                  instanceId: fv!row[recordType!AIA Extraction Instance Field.instanceId],
                  Field: fv!row
                },
                onSuccess: {
                  a!save(ri!count, ri!count + 1),
                  a!save(ri!trigger, now())
                }
              )
            }
          ),
          linkStyle: "STANDALONE",
          showWhen: or(
            a!isNotNullOrEmpty(
              fv!row[recordType!AIA Extraction Instance Field.overrideValue]
            ),
            fv!row[recordType!AIA Extraction Instance Field.overrideNull] = true
          ),
          color: ri!brandingMap.Gray3
        )
      ),
      align: "CENTER",
      width: "ICON"
    ),
    /*Reconicle record action */
    #"SYSTEM_SYSRULES_gridColumn"(
      value: #"SYSTEM_SYSRULES_recordActionField_v1"(
        actions: {
          #"SYSTEM_SYSRULES_recordActionItem"(
