_a-0000edd9-a94f-8000-9bb2-011c48011c48_.sail_code:
a!localVariables(
  /*Classification Model Data*/
  local!classificationModelData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            alias: "activeModelCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Model.publishedAccuracy,
            function: "AVG",
            alias: "accuracyAvg",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null()
  ),
  /*Classification Instance Data*/
  local!classificationInstanceData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Instance,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1),
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.id,
            function: "COUNT",
            alias: "instanceCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.inputTokens,
            function: "SUM",
            alias: "inputTokens",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.outputTokens,
            function: "SUM",
            alias: "outputTokens",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.pageCount,
            function: "SUM",
            alias: "pageCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Classification Instance.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          )
        }
      )
    ).data,
    1,
    null
  ),
  local!modelVersionIds: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Classification Model,
    fields: {
      recordType!AIA Classification Model.publishedModelVersionId
    },
    filters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AIA Classification Model.createdOn,
      operator: "between",
      value: {
        ri!selectedInterval.currrentStartDate,
        ri!selectedInterval.currentEndDate
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 5000)
  ).data[recordType!AIA Classification Model.publishedModelVersionId],
  /*Area of Improvement Data*/
  local!classificationImpData: a!refreshVariable(
    value: rule!AIA_HomePage_Classification_GetDataForAreaOfImprovements(
      brandingMap: ri!brandingMap,
      selectedInterval: ri!selectedInterval
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  /*Accuracy Trend Usage*/
  local!classificationAccuracyTrendUsage: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Classification Instance,
    fields: #"SYSTEM_SYSRULES_aggregationFields"(
      measures: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!selectedInterval.weeklyGroups,
          expression: #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.accuracy,
            function: "AVG",
            alias: concat("avgTrend", fv!index),
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance.createdOn,
                operator: "between",
                value: {
                  todatetime(fv!item.startDate),
                  todatetime(fv!item.endDate)
                }
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance.modelVersionId,
                operator: "in",
                value: { tointeger(local!modelVersionIds) }
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance.statusId,
                operator: "in",
                value: { cons!AIA_INSTANCE_STATUS_RECONCILED_ALL }
              )
            }
          )
        )
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
  ).data,
  /*Instance Run Data*/
  local!classificationInstanceRun: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Classification Instance,
    fields: #"SYSTEM_SYSRULES_aggregationFields"(
      measures: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!selectedInterval.weeklyGroups,
          expression: #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Classification Instance.id,
            function: "COUNT",
            alias: concat("instanceCount", fv!index),
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance.createdOn,
                operator: "between",
                value: {
                  todatetime(fv!item.startDate),
                  todatetime(fv!item.endDate)
                }
              )
            }
          )
        )
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
  ).data,
  local!topClassificationModelDetails: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Classification Model,
    fields: {},
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AIA Classification Model.publishedModelVersionId,
        operator: "not null"
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AIA Classification Model.createdOn,
        operator: "between",
        value: {
          ri!selectedInterval.currrentStartDate,
          ri!selectedInterval.currentEndDate
        }
      )
    },
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: 5,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!AIA Classification Model.publishedVersionInstanceCount
      )
    )
  ).data,
  local!showInstanceRun: rule!AIA_UTIL_isBlank(
    index(
      local!topClassificationModelDetails,
      recordType!AIA Classification Model.totalInputTokens,
      null
    )
  ),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      if(
        a!isNullOrEmpty(
          local!classificationModelData.activeModelCount
        ),
        {
          #"SYSTEM_SYSRULES_sectionLayout_v1"(
            label: #"urn:appian:translation-string:v1:45facea2-44a0-44d8-b555-babf57c98a02",
            labelSize: "SMALL",
            labelColor: ri!brandingMap.Gray4,
            contents: {
              #"SYSTEM_SYSRULES_imageField_v1"(
                labelPosition: "COLLAPSED",
                images: {
                  #"SYSTEM_SYSRULES_documentImage"(
                    document: cons!AIA_IMG_HOME_PAGE_EMPTY_IMAGE
                  )
                },
                size: "MEDIUM",
                isThumbnail: false,
                style: "STANDARD",
                align: "CENTER",
                marginBelow: "STANDARD"
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                label: "",
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: #"urn:appian:translation-string:v1:e5a15e92-ff75-44bf-a236-4fea739bcc0a",
                    color: ri!brandingMap.Gray3
                  )
                },
                align: "CENTER"
              )
            }
          )
        },
