          /*Instances*/
          #"SYSTEM_SYSRULES_gridColumn"(
            label: #"urn:appian:translation-string:v1:396bc6fa-092f-484e-948a-21d48eea366e",
            sortField: recordType!AIA Extraction Model.publishedVersionInstanceCount,
            value: fv!row[recordType!AIA Extraction Model.publishedVersionInstanceCount],
            align: "END"
          ),
          /*Avg Tokens*/
          #"SYSTEM_SYSRULES_gridColumn"(
            label: #"urn:appian:translation-string:v1:a2885374-7f97-4307-b3c7-463db90fce0f",
            value: if(
              or(
                a!isNullOrEmpty(
                  fv!row[recordType!AIA Extraction Model.totalInputTokens]
                ),
                a!isNullOrEmpty(
                  fv!row[recordType!AIA Extraction Model.totalOutputTokens]
                )
              ),
              "-",
              substitute(
                fixed(
                  a!defaultValue(
                    value: sum(
                      fv!row[recordType!AIA Extraction Model.totalInputTokens],
                      fv!row[recordType!AIA Extraction Model.totalOutputTokens]
                    ) / fv!row[recordType!AIA Extraction Model.publishedVersionInstanceCount],
