            value: if(
              a!isNullOrEmpty(
                fv!row[recordType!AIA Extraction Model.totalInputTokens]
              ),
              "-",
              substitute(
                fixed(
                  a!defaultValue(
                    value: fv!row[recordType!AIA Extraction Model.totalInputTokens],
                    default: "-"
                  ),
                  0,
                  false
                ),
                ",",
                ","
              )
            ),
            align: "END"
          ),
          /*Output Tokens*/
          #"SYSTEM_SYSRULES_gridColumn"(
            label: #"urn:appian:translation-string:v1:0ffc200f-4088-4aa5-bda2-ceefcfc53c1f",
            sortField: recordType!AIA Extraction Model.totalOutputTokens,
            value: if(
              a!isNullOrEmpty(
                fv!row[recordType!AIA Extraction Model.totalOutputTokens]
              ),
              "-",
              substitute(
                fixed(
                  a!defaultValue(
                    value: fv!row[recordType!AIA Extraction Model.totalOutputTokens],
                    default: "-"
                  ),
                  0,
                  false
                ),
                ",",
                ","
              )
            ),
            align: "END"
          )
        },
        pageSize: 5,
        initialSorts: #"SYSTEM_SYSRULES_sortInfo"(
