a!localVariables(
  local!firstTry: rule!AIA_ExtractJsonInner(
    json: ri!json
  ),
  local!result: if(
    a!isNotNullOrEmpty(local!firstTry),
    local!firstTry,
    a!localVariables(
      local!secondTry: rule!AIA_ExtractJsonInner(
        json: index(split(ri!json, "Appian's private AI"), 1, null),
      ),
      if(
        a!isNotNullOrEmpty(local!secondTry),
        local!secondTry,
        a!localVariables(
          local!thirdTry: rule!AIA_ExtractJsonInner(
            json: index(split(ri!json, "Appian's private AI"), 2, null),
          ),
          if(
            a!isNotNullOrEmpty(local!thirdTry),
            local!thirdTry,
            a!localVariables(
              local!forthTry: rule!AIA_ExtractJsonInner(
                json: index(split(ri!json, "<output>"), 1, null),
              ),
              if(
                a!isNotNullOrEmpty(local!forthTry),
                local!forthTry,
                a!localVariables(
                  local!fifthTry: rule!AIA_ExtractJsonInner(
                    json: index(split(ri!json, "</entities>"), 1, null),
                  ),
                  local!fifthTry
                )
              )
            )
          )
        )
      )
    )
  ),
  local!finalResult: if(
    a!isNullOrEmpty(local!result),
    null,
    if(
      a!defaultValue(ri!returnList, true),
      local!result,
      if(
        rule!SHRD_IsList(local!result),
        null,
        local!result
      )
    )
  ),
  /* Handle scenario where the result comes back as properties of a $schema */
  if(
    or(
      a!defaultValue(ri!returnList, true),
      a!isNullOrEmpty(local!finalResult),
      not(contains(
        a!keys(local!finalResult),
        "$schema"
      ))
    ),
    local!finalResult,
    local!finalResult.properties
  )
)

