#"SYSTEM_SYSRULES_formLayout_v1"(
  skipAutoFocus: true(),
  label: if(
    ri!isUpdate,
    "Update Extraction Model Version Section",
    "Create Extraction Model Version Section"
  ),
  contents: {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_pickerFieldRecords_v2"(
              label: "Model Version",
              labelPosition: "ABOVE",
              placeholder: "Select a Model Version",
              maxSelections: 1,
              recordType: recordType!AIA Extraction Model Version,
              value: ri!record[recordType!AIA Extraction Model Version Section.modelVersionId],
              saveInto: ri!record[recordType!AIA Extraction Model Version Section.modelVersionId]
            ),
            #"SYSTEM_SYSRULES_textField"(
              label: "Description",
              labelPosition: "ABOVE",
              required: false,
              value: ri!record[recordType!AIA Extraction Model Version Section.description],
              saveInto: ri!record[recordType!AIA Extraction Model Version Section.description],
              characterLimit: 4000
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_textField"(
              label: "Name",
              labelPosition: "ABOVE",
              required: false,
              value: ri!record[recordType!AIA Extraction Model Version Section.name],
              saveInto: ri!record[recordType!AIA Extraction Model Version Section.name],
              characterLimit: 255
            ),
            #"SYSTEM_SYSRULES_textField"(
              label: "Rule",
              labelPosition: "ABOVE",
              required: false,
              value: ri!record[recordType!AIA Extraction Model Version Section.rule],
              saveInto: ri!record[recordType!AIA Extraction Model Version Section.rule],
              characterLimit: 4000
            )
          }
        )
      }
    )
  },
  buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
    primaryButtons: {
      #"SYSTEM_SYSRULES_buttonWidget_v1"(
        label: if(ri!isUpdate, "Save", "Create"),
        submit: true,
        validate: true,
        style: "SOLID"
      )
    },
    secondaryButtons: {
      #"SYSTEM_SYSRULES_buttonWidget_v1"(
        label: "Cancel",
        value: true,
        saveInto: ri!cancel,
        submit: true,
        style: "OUTLINE",
        validate: false
      )
    }
  )
)
