a!localVariables(
  local!brandingMap: a!defaultValue(ri!brandingMap, rule!AIA_UTIL_LoadBrandingMap()),
  local!headerField: a!refreshVariable(
    refreshAlways: true,
    value: #"SYSTEM_SYSRULES_sideBySideLayout"(
      alignVertical: "MIDDLE",
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_headingField"(
            text: ri!title,
            headingTag: ri!headingTag,
            size: a!defaultValue(
              value: ri!headerSize,
              default: "EXTRA_SMALL"
            ),
            marginBelow: "NONE"
          ),
          width: "MINIMIZE"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "info-circle",
              caption: ri!caption,
              color: local!brandingMap.Blue3
            )
          ),
          showWhen: a!isNotNullOrEmpty(ri!caption)
        )
      }
    )
  ),
  local!subHeaderField: a!refreshVariable(
    refreshAlways: true,
    value: #"SYSTEM_SYSRULES_richTextDisplayField"(
      labelPosition: "COLLAPSED",
      showWhen: a!isNotNullOrEmpty(ri!subheader),
      value: {
        char(10),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: ri!subheader,
          color: local!brandingMap.Gray3
        )
      },
      marginBelow: "LESS"
    )
  ),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: a!match(
      value: ri!headerPosition,
      equals: "ADJACENT",
      then: #"SYSTEM_SYSRULES_columnsLayout"(
        alignVertical: "MIDDLE",
        marginAbove: "NONE",
        marginBelow: "NONE",
        spacing: "DENSE",
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              local!headerField,
              local!subHeaderField
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: ri!contents
          )
        }
      ),
      /*default to ABOVE style display*/
      default: {
        local!headerField,
        local!subHeaderField,
        ri!contents
      }
    ),
    height: a!defaultValue(ri!height, "SHORT_PLUS"),
    style: local!brandingMap.DefaultPageBackground,
    shape: "SEMI_ROUNDED",
    padding: "STANDARD",
    marginBelow: "STANDARD",
    showBorder: false
  )
)

_a-0000ed46-3c8c-8000-9bac-011c48011c48_.sail_code:
