    #"urn:appian:translation-variable:v1:cd1d1493-da70-4c8d-a90a-53b50b533f19": #"urn:appian:translation-string:v1:4a0c114d-5821-4d89-94ed-40c5054c27ac"
  ),
  initialSorts: #"SYSTEM_SYSRULES_sortInfo"(
    field: recordType!AIA Classification Model.modifiedOn,
    ascending: false()
  ),
  label: #"urn:appian:translation-string:v1:4a0c114d-5821-4d89-94ed-40c5054c27ac",
  labelPosition: "COLLAPSED",
  data: #"SYSTEM_SYSRULES_recordData"(
    recordType: recordType!AIA Classification Model,
    relatedRecordData: #"SYSTEM_SYSRULES_relatedRecordData"(
      relationship: recordType!AIA Classification Model.modelVersions,
      limit: 1,
      filters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AIA Classification Model Version.status,
        operator: "=",
        value: cons!AIA_MODEL_VERSION_STATUS_PUBLISHED
      ),
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!AIA Classification Model Version.versionId,
        ascending: false
      )
    )
  ),
  columns: {
    /* Model / Most Recent Version / Packet Splitting*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:8b3e37cd-f1d6-4ed5-871a-154a1c7a0156",
      sortField: recordType!AIA Classification Model.name,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: fv!row[recordType!AIA Classification Model.name],
            link: #"SYSTEM_SYSRULES_recordLink"(
              label: fv!row[recordType!AIA Classification Model.name],
              recordType: recordType!AIA Classification Model,
              identifier: fv!identifier
            ),
            linkStyle: "STANDALONE",
            style: "STRONG",
            color: ri!brandingMap.Gray4
          ),
          char(10),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: #"urn:appian:translation-string:v1:0dc23859-ec54-488e-8e0e-54f234016331"(
              #"urn:appian:translation-variable:v1:6432df6d-081e-454e-a970-7f0e3444fc73": fv!row[recordType!AIA Classification Model.publishedVersionNumber]
            ),
            color: ri!brandingMap.Gray3,
            size: "SMALL"
          ),
          if(
            and(
              a!isNotNullOrEmpty(fv!row[recordType!AIA Classification Model.modelVersions]),
              fv!row[recordType!AIA Classification Model.modelVersions][1][recordType!AIA Classification Model Version.allowMultipleOutputs] = true
            ),
            {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: " • ",
                size: "SMALL",
                color: ri!brandingMap.Gray3
              ),
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "files-o",
                color: ri!brandingMap.Gray3,
                size: "SMALL"
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: " " & #"urn:appian:translation-string:v1:6bd0443a-1d14-4267-99bb-364de260265b",
                color: ri!brandingMap.Gray3,
                size: "SMALL"
              )
            },
            {}
          )
        }
      ),
      width: "MEDIUM"
    ),
