    ),
    /*Related Actions*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "",
      value: #"SYSTEM_SYSRULES_recordActionField_v1"(
        actions: {
          #"SYSTEM_SYSRULES_recordActionItem"(
            action: #"urn:appian:record-action:v1:a8b311eb-88c5-453a-a403-318071da677d/81f077e1-7c6c-4677-8a30-5155c6163e6b",
            identifier: fv!row[recordType!AIA Classification Model.publishedModelVersionId]
