_a-0000ec67-5f00-8000-9bf0-011c48011c48_.sail_code:
if(
  a!isNotNullOrEmpty(ri!documentId),
  try(document(ri!documentId, "name"), "[" & #"urn:appian:translation-string:v1:955ba2b6-66dc-4f51-96ff-bc7facbc8d66" & "]"),
  if(
    a!isNotNullOrEmpty(ri!instance),
    a!localVariables(
      local!isExtraction: runtimetypeof(ri!instance) = typeof(recordType!AIA Extraction Instance()),
      if(
        local!isExtraction,
        rule!AIA_FormatDocument(
          documentId: a!defaultValue(
            ri!instance[recordType!AIA Extraction Instance.documentId],
            ri!instance[recordType!AIA Extraction Instance.sourceDocumentId]
          )
        ),
        rule!AIA_FormatDocument(
          documentId: a!defaultValue(
            ri!instance[recordType!AIA Classification Instance.documentId],
            ri!instance[recordType!AIA Classification Instance.sourceDocumentId]
          )
        )
      )
    ),
    "[" & #"urn:appian:translation-string:v1:955ba2b6-66dc-4f51-96ff-bc7facbc8d66" & "]"
  )
)

_a-0000ed46-3c8c-8000-9bac-011c48011c48_.sail_code:
