_a-0000edd9-a94f-8000-9bb2-011c48011c48_.sail_code:
a!localVariables(
  local!version: joinarray(
    #"SYSTEM_SYSRULES_forEach"(
      items: rdrop(ldrop(split(cons!AIA_VERSION, "."), 2), 1),
      expression: tointeger(fv!item)
    ),
    "."
  ),
  concat(
    cons!AIA_DOCUMENTATION_LINK,
    rule!AIA_GetSiteVersion(),
    "/aidc-",
    local!version,
    "/",
    ri!resource,
    if(find(".html", ri!resource) > 0, "", ".html"),
    if(
      a!isNotNullOrEmpty(ri!header),
      "#" & ri!header,
      ""
    )
  )
)

_a-0000ec5c-9eda-8000-9bec-011c48011c48_.sail_code:
a!localVariables(
  local!gridSelection: a!refreshVariable(
    value: {},
    refreshAfter: "RECORD_ACTION"
  ),
  local!numberOfOutcomesLimit: 2,
  #"SYSTEM_SYSRULES_gridField_v2"(
    accessibilityText: #"urn:appian:translation-string:v1:e349de12-26ce-4f07-a2d6-818892336ae7"(
      #"urn:appian:translation-variable:v1:cd1d1493-da70-4c8d-a90a-53b50b533f19": #"urn:appian:translation-string:v1:d84289bc-8889-4003-8cb0-40a82d1652d5"
    ),
    label: "Classifications",
    labelPosition: "COLLAPSED",
    data: #"SYSTEM_SYSRULES_recordData"(
      recordType: recordType!AIA Classification Instance,
      filters: ri!filters,
      relatedRecordData: {
        ri!relatedRecordData,
        #"SYSTEM_SYSRULES_relatedRecordData"(
          relationship: recordType!AIA Classification Instance.outcomes,
          limit: local!numberOfOutcomesLimit,
          /*Only retrieve active outcomes*/
          filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance Outcome.outcomeOverride,
                operator: "is null"
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Instance Outcome.outcomeOverride,
                operator: "=",
                value: cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED
              )
            }
          )
        )
      }
    ),
    columns: {
      /*spot check review flag*/
      #"SYSTEM_SYSRULES_gridColumn"(
        width: "ICON",
        sortField: recordType!AIA Classification Instance.isSamplingRequired,
        value: rule!AIA_richTextDisplaySamplingFlag(
          Instance: fv!row,
          isSpotCheckRequiredField: recordType!AIA Classification Instance.isSamplingRequired,
          brandingMap: ri!brandingMap,
          labelLocation: "CAPTION"
        )
      ),
      /* Instances */
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:11c554c3-1e1c-43a1-9ac8-a846f702995f",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            /* Document Name with Link */
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_FormatDocument(
                instance: fv!row
              ),
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!AIA Classification Instance,
                identifier: fv!identifier
              ),
              linkStyle: "STANDALONE",
              color: ri!brandingMap.Gray4,
              style: "STRONG"
            ),
            char(10),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_UTIL_displayRequestId(
                requestId: fv!row[recordType!AIA Classification Instance.id]
              ) & " • ",
              color: ri!brandingMap.Gray3,
              size: "SMALL"
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              showWhen: contains(
                ri!userFilters,
                #"urn:appian:user-filter:v1:bba819fb-55a0-47e1-bb74-e385f32b6916/c4300508-c3ca-40ba-9f78-fdc590c11504"
              ),
              text: rule!AIA_ModelVersionName(
                ModelVersion: fv!row[recordType!AIA Classification Instance.modelVersion]
              ),
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!AIA Classification Model Version,
                identifier: fv!row[recordType!AIA Classification Instance.modelVersionId],
                targetLocation: #"urn:appian:site-page:v1:15ad6b1a-7c0a-47f8-8803-ac74b16df0df/f6ac5707-0cec-411e-b97a-6e04ee833759"
              ),
              linkStyle: "STANDALONE",
              size: "SMALL"
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              showWhen: not(
                contains(
                  ri!userFilters,
                  #"urn:appian:user-filter:v1:bba819fb-55a0-47e1-bb74-e385f32b6916/c4300508-c3ca-40ba-9f78-fdc590c11504"
                )
              ),
              text: #"urn:appian:translation-string:v1:0dc23859-ec54-488e-8e0e-54f234016331"(
                #"urn:appian:translation-variable:v1:6432df6d-081e-454e-a970-7f0e3444fc73": fv!row[recordType!AIA Classification Instance.modelVersion.versionId]
              ),
              color: ri!brandingMap.Gray3,
              size: "SMALL"
            ),
            if(
              fv!row[recordType!AIA Classification Instance.testInstance],
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: {
                  " • ",
                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "flask", size: "SMALL"),
                  #"urn:appian:translation-string:v1:21705970-1db4-4bc0-9664-f32b86194327"
                },
                color: ri!brandingMap.Gray3,
                size: "SMALL"
              ),
              {}
            )
          }
        ),
        width: if(
          #"SYSTEM_SYSRULES_isPageWidth"({ "DESKTOP", "DESKTOP_WIDE" }),
          "MEDIUM",
          "AUTO"
        )
      ),
      /* Document*/
      #"SYSTEM_SYSRULES_gridColumn"(
        value: rule!AIA_ViewDocumentAction(
          document: fv!row[recordType!AIA Classification Instance.documentId]
        ),
        align: "CENTER",
        width: "ICON_PLUS"
      ),
      /* Status */
      #"SYSTEM_SYSRULES_gridColumn"(
        label: #"urn:appian:translation-string:v1:34413645-ef22-4a94-8709-e4cae57e6a92",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: fv!row[recordType!AIA Classification Instance.refInstanceStatus.icon],
                color: index(
                  ri!brandingMap,
                  fv!row[recordType!AIA Classification Instance.refInstanceStatus.iconColor],
                  null
                ),
                altText: fv!row[recordType!AIA Classification Instance.refInstanceStatus.label],
                caption: fv!row[recordType!AIA Classification Instance.refInstanceStatus.label],

              ),
              " ",
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: fv!row[recordType!AIA Classification Instance.refInstanceStatus.label],
              ),
              rule!AIA_Instance_ValidationTotal(
                row: fv!row,
                field: recordType!AIA Classification Instance.validationErrors,
                brandingMap: ri!brandingMap
              )
            }
          )
        ),
        width: "NARROW_PLUS"
      ),
      /* Errors */
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Errors",
        value: rule!AIA_ValidationErrorsGridDisplay(
          validationErrors: fv!row[recordType!AIA Classification Instance.validationErrors],
          brandingMap: ri!brandingMap
        ),
        align: "CENTER",
        width: "NARROW_PLUS",
        showWhen: cons!AIA_FLAG_SHOW_INSTANCE_ERROR_COLUMN
      ),
      /*Value*/
      #"SYSTEM_SYSRULES_gridColumn"(
        backgroundColor: if(
          fv!row[recordType!AIA Classification Instance.isCorrect],
          null,
          ri!brandingMap.Orange1
        ),
        label: #"urn:appian:translation-string:v1:eeea1e63-d0ca-43a4-a582-185e72f25f41",
        value: if(
          fv!row[recordType!AIA Classification Instance.isCorrect],
          /*Non-Overridden Display*/
          concat(
            joinarray(
              fv!row[recordType!AIA Classification Instance.outcomes.classification],
              ", "
            ),
            if(
              fv!row[recordType!AIA Classification Instance.activeOutcomeCount] > local!numberOfOutcomesLimit,
              " " & #"urn:appian:translation-string:v1:15baaea6-3a50-410c-a1ad-f419be1e3e86"(
                #"urn:appian:translation-variable:v1:a1a3f4c3-c64b-47bc-8a89-5a968358d99d": fv!row[recordType!AIA Classification Instance.activeOutcomeCount] - local!numberOfOutcomesLimit
              ),
              ""
            )
          ),
          /*Override Display*/
          if(
            fv!row[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs],
            /*Multiple Outcome Override Display*/
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: #"SYSTEM_SYSRULES_richTextBulletedList"(
                items: {
                  /*Overridden Categories*/
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    showWhen: and(
                      a!isNotNullOrEmpty(fv!row[recordType!AIA Classification Instance.classificationOverrideOutcomeCount]),
                      fv!row[recordType!AIA Classification Instance.classificationOverrideOutcomeCount] > 0
                    ),
                    text: {
                      if(
                        fv!row[recordType!AIA Classification Instance.classificationOverrideOutcomeCount] = 1,
                        fv!row[recordType!AIA Classification Instance.classificationOverrideOutcomeCount] & " " & #"urn:appian:translation-string:v1:dca3f237-770e-4f9e-834f-23edfbf0ab8a" & " ",
                        fv!row[recordType!AIA Classification Instance.classificationOverrideOutcomeCount] & " " & #"urn:appian:translation-string:v1:1b70fe4e-16c8-4dc8-90c0-094ca2ed8e4d" & " "
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24",
                        style: "STRONG"
                      )
                    }
                  ),
                  /*Overridden Page Splits*/
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    showWhen: and(
                      a!isNotNullOrEmpty(fv!row[recordType!AIA Classification Instance.splitPagesJsonOverrideOutcomeCount]),
                      fv!row[recordType!AIA Classification Instance.splitPagesJsonOverrideOutcomeCount] > 0
                    ),
                    text: {
                      if(
                        fv!row[recordType!AIA Classification Instance.splitPagesJsonOverrideOutcomeCount] = 1,
                        fv!row[recordType!AIA Classification Instance.splitPagesJsonOverrideOutcomeCount] & " " & #"urn:appian:translation-string:v1:9fd92575-4208-4f90-beea-a6697c53ff07" & " ",
                        fv!row[recordType!AIA Classification Instance.splitPagesJsonOverrideOutcomeCount] & " " & #"urn:appian:translation-string:v1:cd9ec65a-972c-4cbb-afe2-52b5d7b7675d" & " "
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24",
                        style: "STRONG"
                      )
                    }
                  ),
                  /*Added Outcomes*/
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    showWhen: and(
                      a!isNotNullOrEmpty(fv!row[recordType!AIA Classification Instance.addedOutcomeCount]),
                      fv!row[recordType!AIA Classification Instance.addedOutcomeCount] > 0
                    ),
                    text: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: fv!row[recordType!AIA Classification Instance.addedOutcomeCount],
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: " " & #"urn:appian:translation-string:v1:65e11f66-7745-4827-8976-6028c711a1a4",
                        style: "STRONG"
                      )
                    },
                    color: ri!brandingMap.Green4
                  ),
                  /*Removed Outcomes*/
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    showWhen: and(
                      a!isNotNullOrEmpty(fv!row[recordType!AIA Classification Instance.removedOutcomeCount]),
                      fv!row[recordType!AIA Classification Instance.removedOutcomeCount] > 0
                    ),
                    text: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: fv!row[recordType!AIA Classification Instance.removedOutcomeCount],
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: " " & #"urn:appian:translation-string:v1:7ecab859-1f80-402d-9027-b2f0a0721fe6",
                        style: "STRONG"
                      )
                    },
                    color: ri!brandingMap.Red4
                  )
                }
              )
            ),
            /*Single Outcome Override Display*/
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  color: ri!brandingMap.Gray3,
                  text: fv!row[recordType!AIA Classification Instance.outcomes.classification][1] & " "
                ),
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "arrow-right",
                  color: ri!brandingMap.Gray3,
                  altText: #"urn:appian:translation-string:v1:b9f8426e-587d-4944-8fe1-c1ec27a893b6"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " " & fv!row[recordType!AIA Classification Instance.outcomes.classificationOverride][1],
                  style: "STRONG"
                )
              }
            )
          )
        ),
        width: "NARROW_PLUS"
      ),
      /* Initiated */
      #"SYSTEM_SYSRULES_gridColumn"(
        align: "END",
        label: #"urn:appian:translation-string:v1:cda32959-933a-49f8-999f-971eeaa0c035",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_UTIL_displayDateTime(
                isDisplayTime: true(),
                dateTime: fv!row[recordType!AIA Classification Instance.createdOn]
              ),
              color: ri!brandingMap.Gray4,
              size: "SMALL"
            ),
            char(10),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AIA_UTIL_displayUserFullName(
                userName: fv!row[recordType!AIA Classification Instance.createdBy]
              ),
              color: ri!brandingMap.Gray3,
              size: "SMALL"
            )
          }
        ),
        width: "NARROW_PLUS"
      ),
      /* Reconciled */
      #"SYSTEM_SYSRULES_gridColumn"(
        align: "END",
        label: #"urn:appian:translation-string:v1:6cfac926-65bd-47cf-be37-303ba4c01ed1",
        value: if(
          a!isNullOrEmpty(fv!row[recordType!AIA Classification Instance.reconciledOn]),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(text: "-", color: ri!brandingMap.Gray4)
            }
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AIA_UTIL_displayDateTime(
                  isDisplayTime: true(),
                  dateTime: fv!row[recordType!AIA Classification Instance.reconciledOn]
                ),
                color: ri!brandingMap.Gray4,
                size: "SMALL"
              ),
              char(10),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: a!defaultValue(
                  rule!AIA_UTIL_displayUserFullName(
                    userName: fv!row[recordType!AIA Classification Instance.reconciledBy]
                  ),
                  "Test Case"
                ),
                color: ri!brandingMap.Gray3,
                size: "SMALL"
              )
            }
          )
        ),
        width: "NARROW_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "",
        value: #"SYSTEM_SYSRULES_recordActionField_v1"(
