a!localVariables(
  local!boundingBoxesForSpanIds: rule!AIA_ExtractJson(
    json: rule!AIA_PLG_QueryFile(
      document: ri!boundingBoxDoc,
      jsonPath: "$[?(@.id in ['" & joinarray(ri!spanIds, "','") & "'])]"
    )
  ),
  if(
    /*If no corresponding bounding boxes were returned for the inputted spanIds, return null*/
    a!isNullOrEmpty(local!boundingBoxesForSpanIds),
    null,
    if(
      /*If at least one of the returned bounding boxes has a confidence attached, use that*/
      a!isNotNullOrEmpty(
        reject(
          a!isNullOrEmpty,
          local!boundingBoxesForSpanIds.confidenceScore
        )
      ),
      min(local!boundingBoxesForSpanIds.confidenceScore),
      /*Otherwise, for each returned bounding box for the inputted spanIds, search for a nearby "word" dictionary 
      in the bounding boxes doc that matches the inputted extracted value*/
      a!localVariables(
        local!wordTypeBoundingBoxes: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!boundingBoxesForSpanIds,
            expression: index(
              rule!AIA_ExtractJson(
                json: rule!AIA_PLG_QueryFile(
                  document: ri!boundingBoxDoc,
                  jsonPath: concat(
                    "$[?(@.type == 'word' && @.text == '",
                    ri!extractedValue,
                    "' && @.pageNumber == ",
                    fv!item.pageNumber,
                    /*The following 4 filters ensure top left point of the "word" bounding box is contained in the larger one*/
                    " && @.geometry.top >= ",
                    fv!item.geometry.top,
                    " && @.geometry.top <= ",
                    todecimal(fv!item.geometry.top) + todecimal(fv!item.geometry.height),
                    " && @.geometry.left >= ",
                    fv!item.geometry.left,
                    " && @.geometry.left <= ",
                    todecimal(fv!item.geometry.left) + todecimal(fv!item.geometry.width),
                    ")]"
                  )
                )
              ),
              1,
              {}
            )
          )
        ),
        if(
          and(
            a!isNotNullOrEmpty(
              reject(
                a!isNullOrEmpty,
                local!wordTypeBoundingBoxes
              )
            ),
            a!isNotNullOrEmpty(
              reject(
                a!isNullOrEmpty,
                local!wordTypeBoundingBoxes.confidenceScore
              )
            )
          ),
          min(local!wordTypeBoundingBoxes.confidenceScore),
          /*Lastly, if there were no "word" dictionaries that matched the inputted extracted value, search for ALL
          "word" dictionaries contained inside the original bounding boxes and take the minimum of all of their confidence.
          This is done in case the extracted value is additionally parsed or formatted differently in the bounding boxes doc*/
          a!localVariables(
            local!allContainedWordBoundingBoxes: a!flatten(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!boundingBoxesForSpanIds,
                expression: index(
                  rule!AIA_ExtractJson(
                    json: rule!AIA_PLG_QueryFile(
                      document: ri!boundingBoxDoc,
                      jsonPath: concat(
                        "$[?(@.type == 'word' && @.pageNumber == ",
                        fv!item.pageNumber,
                        /*The following 4 filters ensure top left point of the "word" bounding box is contained in the larger one*/
                        " && @.geometry.top >= ",
                        fv!item.geometry.top,
                        " && @.geometry.top <= ",
                        todecimal(fv!item.geometry.top) + todecimal(fv!item.geometry.height),
                        " && @.geometry.left >= ",
                        fv!item.geometry.left,
                        " && @.geometry.left <= ",
                        todecimal(fv!item.geometry.left) + todecimal(fv!item.geometry.width),
                        ")]"
                      )
                    )
                  ),
                  1,
                  {}
                )
              )
            ),
            if(
              and(
                a!isNotNullOrEmpty(
                  reject(
                    a!isNullOrEmpty,
                    local!allContainedWordBoundingBoxes
                  )
                ),
                a!isNotNullOrEmpty(
                  reject(
                    a!isNullOrEmpty,
                    local!allContainedWordBoundingBoxes.confidenceScore
                  )
                )
              ),
              min(local!allContainedWordBoundingBoxes.confidenceScore),
              null
            )
          )
        )
      )
    )
  )
)

