sail_code:
{
  a!localVariables(
    local!invalidExtensions: difference(
      upper(
        ri!files.extension
      ),
      "PDF"
    ),
    if(
      length(
        local!invalidExtensions
      ) > 0,
      "Attachments must be pdf files",
      ""
    )
  ),
  if(
    or(
      ri!files.size > 10000000
    ),
    "Attachments may not exceed 10MB",
    null
  )
}
