sail_code:
#"SYSTEM_SYSRULES_headerContentLayout"(
  header: #"SYSTEM_SYSRULES_cardLayout"(
    showBorder: false,
    style: "ACCENT",
    contents: {
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: #"SYSTEM_SYSRULES_richTextHeader"(
                text: ri!label,
                size: "LARGE"
              )
            )
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            showWhen: a!isNotNullOrEmpty(ri!actions),
            item: ri!actions
          )
        }
      )
    }
  ),
  backgroundColor: ri!backgroundColor,
  contentsPadding: ri!contentsPadding,
  contents: ri!contents
)
