sail_code:
if(
  ri!isDate,
  {
    eomonth(today(), - 1 - ri!monthsAgo) + 1,
    if(
      ri!monthsAgo = 0,
      today(),
      edate(today(), - ri!monthsAgo)
    )
  },
  if(
    ri!monthsAgo = 0,
    {
      rule!SHRD_CalculateUserDatetime(gmtDatetime: eomonth(today(), - 1) + 2),
      now()
    },
    {
      rule!SHRD_CalculateUserDatetime(
        gmtDatetime: eomonth(today(), - 1 - ri!monthsAgo) + 2
      ),
      if(
        isleapyear(year(now())),
        now() - 366,
        now() - 365
      )
    }
  )
)
