sail_code:
if(
  and(
    a!isNotNullOrEmpty(ri!ModelVersionField),
    or(
      ri!showWhen,
      isnull(ri!showWhen),
    )
  ),
  a!localVariables(
    local!resultFieldIndex: index(
      wherecontains(
        tostring(ri!ModelVersionField[recordType!AIA Extraction Model Version Field.fieldId]),
        touniformstring(ri!ExtractionInstanceFields[recordType!AIA Extraction Instance Field.modelVersionFieldId])
      ),
      1
    ),
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      preventWrapping: true,
      labelPosition: "COLLAPSED",
      value: #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AIA_DisplayPercentage(
          value: ri!ExtractionInstanceFields[local!resultFieldIndex][recordType!AIA Extraction Instance Field.modelVersionField.correctFieldPercentage]
        ),
        color: rule!AIA_PercentageColor(
          value: ri!ExtractionInstanceFields[local!resultFieldIndex][recordType!AIA Extraction Instance Field.modelVersionField.correctFieldPercentage],
          isBackground: false
        )
      ),
    )
  ),
  {}
)
