sail_code:
{
  rule!SHRD_BooleanSlider(
    label: "Confidence Threshold",
    value: ri!record[recordType!AIA Classification Model Version.confidenceThreshold],
    precision: 5,
    labelPosition: "ABOVE",
    saveInto: ri!record[recordType!AIA Classification Model Version.confidenceThreshold],
    showWhen: ri!record[recordType!AIA Classification Model Version.model.type] = cons!AIA_EXTRACTION_TYPES[2]
  ),
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: "Options",
    labelSize: "MEDIUM_PLUS",
    labelHeadingTag: "H2",
    labelColor: "STANDARD",
    contents: {
      #"SYSTEM_SYSRULES_gridLayout"(
        labelPosition: "COLLAPSED",
        headerCells: {
          #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
            label: "Name"
          ),
          #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
            label: "Description"
          ),
          #"SYSTEM_SYSRULES_gridLayoutHeaderCell"()
        },
        columnConfigs: {
          #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
            width: "DISTRIBUTE",
            weight: "1X"
          ),
          #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
            width: "DISTRIBUTE",
            weight: "2X"
          ),
          #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
            width: "ICON"
          )
        },
        rows: #"SYSTEM_SYSRULES_forEach"(
          items: ri!record[recordType!AIA Classification Model Version.categories],
          expression: #"SYSTEM_SYSRULES_gridRowLayout"(
            id: fv!index,
            contents: {
              #"SYSTEM_SYSRULES_textField"(
                value: fv!item[recordType!AIA Classification Model Version Category.name],
                saveInto: fv!item[recordType!AIA Classification Model Version Category.name],
                required: true
              ),
              #"SYSTEM_SYSRULES_textField"(
                value: fv!item[recordType!AIA Classification Model Version Category.description],
                saveInto: fv!item[recordType!AIA Classification Model Version Category.description],
                required: false
              ),
              #"SYSTEM_SYSRULES_imageField_v1"(
                images: #"SYSTEM_SYSRULES_documentImage"(
                  document: #"SYSTEM_SYSRULES_iconIndicator"(
                    icon: "REMOVE"
                  ),
                  altText: "Remove Row",
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    value: fv!index,
                    saveInto: {
                      a!save(
                        ri!deletedOptionsRecords,
                        fn!if(
                          a!isNotNullOrEmpty(
                            ri!record[recordType!AIA Classification Model Version.categories][save!value][recordType!AIA Classification Model Version Category.id]
                          ),
                          append(
                            ri!deletedOptionsRecords,
                            ri!record[recordType!AIA Classification Model Version.categories][save!value]
                          ),
                          ri!deletedOptionsRecords
                        )
                      ),
                      a!save(
                        ri!record[recordType!AIA Classification Model Version.categories],
                        remove(
                          ri!record[recordType!AIA Classification Model Version.categories],
                          save!value
                        )
                      )
                    }
                  )
                ),
                size: "ICON"
              )
            },
          )
        ),
        validations: if(
          a!isNullOrEmpty(ri!record[recordType!AIA Classification Model Version.categories]),
          "Options required",
          null
        ),
        height: "SHORT_PLUS",
        addRowLink: #"SYSTEM_SYSRULES_dynamicLink"(
          label: "Add Row",
          value: {
            recordType!AIA Classification Model Version.categories: {
              recordType!AIA Classification Model Version Category()
            }
          },
          saveInto: a!save(
            ri!record[recordType!AIA Classification Model Version.categories],
            append(
              ri!record[recordType!AIA Classification Model Version.categories],
              save!value
            )
          )
        ),
        shadeAlternateRows: true
      )
    },
    isCollapsible: false
  )
}
