  tointeger(ri!newLine.pageNumber) = tointeger(ri!processed.pageNumber),
  a!map(
    pageNumber: ri!newLine.pageNumber,
    text: ri!processed.text & char(10) & ri!newLine.text
  ),
  a!map(
    pageNumber: ri!newLine.pageNumber,
    text: ri!processed.text & char(10) & 
