sail_code:
ri!processName & " - ID " & ri!ExtractionInstance[recordType!AIA Extraction Instance.id] &
if(
  and(
    a!isNullOrEmpty(ri!SectionInstance),
    a!isNotNullOrEmpty(ri!batch),
  ),
  " - No Section - Batch " & ri!batch,
  if(
    and(
      a!isNotNullOrEmpty(ri!SectionInstance),
      a!isNullOrEmpty(ri!batch)
    ),
    " - Section " & ri!SectionInstance[recordType!AIA Extraction Instance Section.id],
    if(
      and(
        a!isNotNullOrEmpty(ri!SectionInstance),
        a!isNotNullOrEmpty(ri!batch)
      ),
      " - Section " & ri!SectionInstance[recordType!AIA Extraction Instance Section.id] & " - Batch " & ri!batch,
      ""
    )
  )
)
