sail_code:
a!localVariables(
  local!aliases: rule!AIA_RULE_ENUM_Aliases(),
  local!fix: #"SYSTEM_SYSRULES_forEach"(
    items: local!aliases,
    expression: a!map(
      key: upper(
        fv!item.originalWord
      ),
      values: apply(
        fn!upper,
        fv!item.aliases
      )
    )
  ),
  local!normalizeString: {reduce(
    rule!AIA_RULE_NormalizeStringHelper(text: _, abbreviation: _, ),
    upper(ri!string),
    local!fix
  )},
  trim(local!normalizeString)
)
