sail_code:
a!localVariables(
  local!result: jsonexpression(rule!AIA_ToJson(data: ri!map), ri!expression),
  local!firstChar: if(
    a!isNotNullOrEmpty(local!result),
    left(local!result, 1),
    " "
  ),
  if(
    or(
      local!firstChar = "{",
      local!firstChar = "["
    ),
    rule!AIA_ExtractJson(json: local!result),
    stripwith(local!result, """")
  )
)
