sail_code:
if(
  a!isNullOrEmpty(ri!logicalExpression.rule),
  null,
  a!localVariables(
    local!description: rulebyname(ri!logicalExpression.rule).description,
    local!brandingMap: rule!AIA_UTIL_LoadBrandingMap(),
    local!showMore: false(),
    local!truncateLength: 75,
    if(
      ri!isReturnOnlyText = true(),
      concat(ri!logicalExpression.rule, " - ", local!description),
      {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          color: local!brandingMap.Gray4,
          style: "PLAIN",
          text: ri!logicalExpression.rule
        ),
        if(
          a!isNullOrEmpty(local!description),
          {},
          if(
            or(
              len(local!description) <= local!truncateLength,
              a!defaultValue(
                value: ri!showMoreLink,
                default: true
              ) <> true()
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              color: local!brandingMap.Gray3,
              text: " - " & local!description
            ),
            {
              " - ",
              /*Full Text*/
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                color: local!brandingMap.Gray3,
                text: local!description,
                showWhen: local!showMore
              ),
              /*Abbreviated Text*/
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                color: local!brandingMap.Gray3,
                text: left(local!description, local!truncateLength),
                showWhen: not(local!showMore)
              ),
              /*See More Link*/
              {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  color: local!brandingMap.Gray3,
                  text: "... ",
                  showWhen: local!showMore = false
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"urn:appian:translation-string:v1:82bb2717-336d-401b-9fac-5bb607526bff",
                  style: "PLAIN",
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    label: #"urn:appian:translation-string:v1:82bb2717-336d-401b-9fac-5bb607526bff",
                    value: true,
                    saveInto: local!showMore
                  ),
                  linkStyle: "STANDALONE",
                  showWhen: not(local!showMore)
                )
              },
              /*See Less Link*/
              {
                #"SYSTEM_SYSRULES_richTextItem_v1"(text: " ", showWhen: local!showMore = true),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"urn:appian:translation-string:v1:09584167-cb6b-42d9-9b9d-82b5e71b2d69",
                  style: "PLAIN",
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    label: #"urn:appian:translation-string:v1:09584167-cb6b-42d9-9b9d-82b5e71b2d69",
                    value: false,
                    saveInto: local!showMore
                  ),
                  linkStyle: "STANDALONE",
                  showWhen: local!showMore = true
                )
              }
            }
          )
        )
      }
    )
  )
)
