sail_code:
concat(
  ri!pmName,
  " - ",
  if(
    a!isNotNullOrEmpty(ri!id),
    ri!id,
    tostring(ri!recordIdField) & ": " & 
    if(
      /*Can't use a!isNullOrEmpty() here to account for the `{null}` condition*/
      or(
        a!isNullOrEmpty(ri!primaryRecordPv),
        a!isNullOrEmpty(index(ri!primaryRecordPv, ri!recordIdField, null))
      ),
      "null/empty",
      joinarray(
        a!flatten(ri!primaryRecordPv[ri!recordIdField]),
        ", "
      )
    )
  )
)
