sail_code:
{
  #"SYSTEM_SYSRULES_cardLayout"(
    shape: ri!brandingMap.CardShape,
    padding: "STANDARD",
    contents: {
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            width: "3X",
            item: #"SYSTEM_SYSRULES_textField"(
              label: #"urn:appian:translation-string:v1:1d2ee947-3116-4cca-8a1f-26fe5bcc2914",
              labelPosition: "ABOVE",
              value: ri!extractionModel[recordType!AIA Extraction Model.name],
              saveInto: {
                a!save(
                  ri!extractionModel[recordType!AIA Extraction Model.name],
                  if(
                    a!isNullOrEmpty(save!value),
                    save!value,
                    trim(save!value)
                  )
                ),
                a!save(
                  ri!extractionModel[recordType!AIA Extraction Model.key],
                  rule!SHRD_ReformatLabelAsKey(
                    label: ri!extractionModel[recordType!AIA Extraction Model.name]
                  )
                )
              },
              characterLimit: 100,
              required: true,
              showCharacterCount: false,
              validations: if(
                a!isNotNullOrEmpty(
                  rule!AIA_Extraction_GetModelByKey(
                    key: ri!ExtractionModel[recordType!AIA Extraction Model.key]
                  )
                ),
                "Model key must be unique",
                null
              ),

            ),

          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            width: "1X",
            item: #"SYSTEM_SYSRULES_textField"(
              label: #"urn:appian:translation-string:v1:2b3a2e79-0b85-4b52-bbf6-b2e8d08f1447",
              readOnly: true,
              value: if(
                a!isNotNullOrEmpty(
                  ri!ExtractionModel[recordType!AIA Extraction Model.key]
                ),
                ri!ExtractionModel[recordType!AIA Extraction Model.key],
                "-"
              )
            )
          )
        }
      ),
      #"SYSTEM_SYSRULES_paragraphField"(
        height: "SHORT",
        label: #"urn:appian:translation-string:v1:be96096c-d32a-4708-a91d-b5dda4f2f30c",
        labelPosition: "ABOVE",
        value: ri!extractionModel[recordType!AIA Extraction Model.description],
        saveInto: ri!extractionModel[recordType!AIA Extraction Model.description],
        characterLimit: 255,
        required: false,
        showCharacterCount: false
      )
    },
    showBorder: ri!brandingMap.CardShowBorder,
    style: ri!brandingMap.AppianBackground,
    marginBelow: "STANDARD"
  ),
  rule!AIA_Extraction_Sub_ModelAdvancedSettings(
    extractionModel: ri!extractionModel,
    brandingMap: ri!brandingMap
  )
}
