sail_code:
tostring(
  a!match(
    value: ri!value,
    /*If Percentage Greater for 1000*/
    whenTrue: ri!value > 1000,
    then: null,
    /*High*/
    whenTrue: ri!value > .8,
    then: ri!brandingMap.ChartOneGreen,
    /*Medium*/
    whenTrue: ri!value > .5,
    then: ri!brandingMap.ChartOneYellow,
    /*Low*/
    whenTrue: ri!value > .25,
    then: ri!brandingMap.ChartOneOrange,
    /*Very Low*/
    default: ri!brandingMap.ChartOneRed
  )
)
