a!localVariables(
  local!brandingMap: rule!AIA_UTIL_LoadBrandingMap(),
  #"SYSTEM_SYSRULES_headerContentLayout"(
    contents: {
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: #"SYSTEM_SYSRULES_sectionLayout_v1"(
          labelHeadingTag: "H2",
          labelSize: "SMALL",
          labelColor: local!brandingMap.Gray4,
          contents: rule!AIA_ClassificationModelVersion_Prompt_TokenKPI(
            brandingMap: local!brandingMap,
            modelVersionId: ri!ModelVersion[recordType!AIA Classification Model Version.modelVersionId]
          )
        ),
        style: local!brandingMap.White,
        showBorder: false(),
        marginBelow: "STANDARD"
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_cardLayout"(
              contents: #"SYSTEM_SYSRULES_sectionLayout_v1"(
                label: #"urn:appian:translation-string:v1:ab6ce936-2015-4e58-a2d1-a10bcd2372e4",
                labelHeadingTag: "H2",
                labelSize: "SMALL",
                labelColor: local!brandingMap.Gray4,
                contents: #"SYSTEM_SYSRULES_cardLayout"(
                  padding: "LESS",
                  height: "AUTO",
                  style: local!brandingMap.AppianBackground,
                  marginAbove: "NONE",
                  marginBelow: "NONE",
                  showBorder: local!brandingMap.CardShowBorder,
                  contents: #"SYSTEM_SYSRULES_paragraphField"(
                    labelPosition: "COLLAPSED",
                    value: rule!AIA_Classification_GeneratePrompt(modelVersion: ri!ModelVersion, ),
                    readOnly: true
                  )
                )
              ),
              style: local!brandingMap.White,
              showBorder: false()
            ),

          ),
          /*Prompt Examples*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_cardLayout"(
              contents: #"SYSTEM_SYSRULES_sectionLayout_v1"(
                label: #"urn:appian:translation-string:v1:50f1d007-13c9-4e75-b540-e96bc09a3968",
                labelHeadingTag: "H2",
                labelSize: "SMALL",
                labelColor: local!brandingMap.Gray4,
                contents: if(
                  a!isNullOrEmpty(
                    ri!ModelVersion[recordType!AIA Classification Model Version.examples]
                  ),
                  rule!AIA_UTIL_displayEmptyMessage(
                    emptyMessage: #"urn:appian:translation-string:v1:b33caf0b-37ef-4a87-b284-1a23eb79c9e8",
                    brandingMap: local!brandingMap
                  ),
                  #"SYSTEM_SYSRULES_forEach"(
                    items: ri!ModelVersion[recordType!AIA Classification Model Version.examples],
                    expression: {
                      #"SYSTEM_SYSRULES_boxLayout"(
                        showBorder: false,
                        label: #"urn:appian:translation-string:v1:e95689a9-6b4a-4515-a7f3-f683f15bf0c7"(
                          #"urn:appian:translation-variable:v1:458c3433-d183-4858-a240-e9805a843147": fv!index
                        ),
                        labelHeadingTag: "H3",
                        padding: "NONE",
                        contents: {
                          #"SYSTEM_SYSRULES_cardLayout"(
                            contents: {
                              /*Example Input Value*/
                              #"SYSTEM_SYSRULES_paragraphField"(
                                readOnly: true,
                                label: #"urn:appian:translation-string:v1:4a6eaf6a-0039-4c71-abd0-e740b0eea194",
                                labelPosition: "ABOVE",
                                value: if(
                                  a!isNullOrEmpty(
                                    fv!item[recordType!AIA Model Version Example.input]
                                  ),
                                  "-",
                                  fv!item[recordType!AIA Model Version Example.input]
                                ),

                              ),
                              /*Example Output Value*/
                              #"SYSTEM_SYSRULES_paragraphField"(
                                readOnly: true,
                                label: #"urn:appian:translation-string:v1:aa3725db-5df9-4b9f-9caa-8e203e558de8",
                                labelPosition: "ABOVE",
                                value: if(
                                  a!isNullOrEmpty(
                                    fv!item[recordType!AIA Model Version Example.output]
                                  ),
                                  "-",
                                  fv!item[recordType!AIA Model Version Example.output]
                                ),

                              ),
                              /*Example Rule Value*/
                              #"SYSTEM_SYSRULES_paragraphField"(
                                readOnly: true,
                                label: #"urn:appian:translation-string:v1:af1495f1-ddf0-446b-a3f4-5bacc9101672",
                                labelPosition: "ABOVE",
                                value: if(
                                  a!isNullOrEmpty(
                                    fv!item[recordType!AIA Model Version Example.rule]
                                  ),
                                  "-",
                                  fv!item[recordType!AIA Model Version Example.rule]
                                ),

                              )
                            },
                            style: local!brandingMap.DefaultPageBackground,
                            showBorder: false()
                          )
                        },
                        isCollapsible: true(),
                        style: local!brandingMap.Blue1,
                        marginBelow: "STANDARD"
                      )
                    }
                  )
                )
              ),
              style: local!brandingMap.White,
              showBorder: false()
            )
          )
        }
      )
    },
    backgroundColor: local!brandingMap.AppianBackground
  )
)
