sail_code:
a!localVariables(
  local!intervalRange: a!match(
    value: ri!type,
    equals: cons!AIA_UTIL_TEXT_INTERVAL_TYPE[1],
    then: a!map(
      from: userdatetime(ri!year, 1, 1),
      to: userdatetime(ri!year + 1, 1, 1)
    ),
    equals: cons!AIA_UTIL_TEXT_INTERVAL_TYPE[2],
    then: a!map(
      from: userdatetime(ri!year, ri!month, 1),
      to: userdatetime(ri!year, ri!month + 1, 1)
    ),
    default: {}
  ),
  local!intervalRange
)
