sail_code:
{
  #"SYSTEM_SYSRULES_sideBySideLayout"(
    items: {
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: #"urn:appian:translation-string:v1:24d35aad-d22e-454b-8569-b27fe7c1aee9",
            color: ri!brandingMap.Gray4,
            style: "STRONG"
          )
        )
      ),
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_recordActionField_v1"(
          actions: #"SYSTEM_SYSRULES_recordActionItem"(
            action: #"urn:appian:record-action:v1:64322398-e457-4695-87d5-09794164cf77/7e21971b-39a4-4890-af13-99a5f749a6b5",
            identifier: ri!extractionInstanceId
          ),
          style: "TOOLBAR",
          display: "LABEL",
          align: "END",
          showWhen: ri!isShowAction = true()
        ),
        width: "MINIMIZE"
      )
    },
    showWhen: ri!showTitle,
    marginAbove: "STANDARD"
  ),
  if(
    a!isNullOrEmpty(ri!instanceErrors),
    {
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: {
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "thumbs-up",
                color: ri!brandingMap.Gray3,
                size: "MEDIUM_PLUS"
              )
            },
            align: "CENTER",
            marginAbove: "STANDARD"
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: #"urn:appian:translation-string:v1:ba8c18ad-393c-4621-ae6d-6bf726c006b1",
                color: ri!brandingMap.Gray3
              )
            },
            align: "CENTER",
            marginBelow: "MORE"
          )
        },
        style: ri!brandingMap.AppianBackground,
        showBorder: false

      )
    },
    {
      #"SYSTEM_SYSRULES_forEach"(
        items: index(
          ri!instanceErrors,
          wherecontains(
            tointeger({cons!AIA_VALIDATION_TYPE_ID_FIELD, cons!AIA_VALIDATION_TYPE_ID_AI_REVIEW}),
            tointeger(ri!instanceErrors[recordType!AIA Instance Validation Error.errorType.id])
          ),
          {}
        ),
        expression: {
          if(
            fv!isFirst,
            {},
            #"SYSTEM_SYSRULES_horizontalLine"(
              marginAbove: "EVEN_LESS",
              marginBelow: "LESS"
            )

          ),
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AIA_RULE_DisplayValidationRulePriority(
                  validation: fv!item,
                  brandingMap: ri!brandingMap
                ),
                width: "MINIMIZE"
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                  value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: rule!AIA_DisplayValidationMessageForInstance(
                      validationInstance: fv!item,
                      brandingMap: ri!brandingMap
                    ),
                    color: ri!brandingMap.Gray4
                  )
                ),
                width: "MINIMIZE"
              )
            }
          )
        }
      )
    }
  )
}
